import React, { Component } from 'react';
import '../../assets/css/home/homeCenterPanel.css';
import RenderMatch from './renderMatch';

class Match extends Component {
    constructor(props) {
        super(props)
        this.state = {
            match: '',
            matchType: '',
        }
    }

    printMatchCardHeader(printSeriesTitle, seriesTitle) {
        if (printSeriesTitle === 0) {
            return <h6 className="card-title">
                       {seriesTitle}
                       <hr/> 
                   </h6>
        }
        return;
    }

    printVideosCardHeader(printSeriesTitle, match) {

        if (printSeriesTitle === 0) {

            return (
                <div className='card'>
                    <div className='card-body p-3'>
                        <img className='center-panel-med-image'
                            src={match.med_image} alt="no Iamge" />
                           <div ClassName="home-center-videos-title">
                               {match.title} 
                           </div>
                    </div>
                </div>
            )

        }
        else {

            return (
                <div className='card'>
                    {/* <img className='center-panel-med-image'
                        src={match.med_image} alt="no Iamge" /> */}
                    <h6>{match.title} </h6>
                </div>
            )

        }
    }

    render() {
        const { match, matchType, printSeriesTitle , seriesTitle} = this.props;
        if (matchType === 'matches') {
            return ( 
                <div className="card card-block mb-1" style={{ minHeight: 75 + "px" }}>
                    <div className="card-body p-3">
                        {this.printMatchCardHeader(printSeriesTitle, seriesTitle)}
                        <RenderMatch
                            match={match}
                            matchType={matchType}
                        />
                    </div>
                </div>
            );      
        }

        if (matchType === 'news') {
            return (
                <div>  
                     <ul>
                            <li>{match.title}</li>
                     </ul>
                </div>
            )
        }

        if (matchType === 'videos') {
            return (
                <div>
                    {/* Following Code Render Flags or team B */}
                    {this.printVideosCardHeader(printSeriesTitle, match)}
                </div>
           )
        }

        // if (matchType === 'article') {
        //     return (
        //         <div className="flex-d">
        //             <div className="card block-card card-home-center-articles">

        //                 <div style={{width:75 +"%", float:'left' }} >
        //                     <h6>{match.title} </h6>
        //                 </div>

        //                 <div  style={{width:25 +"%", float:'left'}} >
        //                     <img style={{ height: 100 + "px", width: 150 + "px" }}
        //                         src={match.image}
        //                         alt="no Iamge" />
        //                 </div>

        //             </div>       
        //         </div>
        //     )
        // }

        // if (matchType === 'tweets') {
            
        //     return (
        //         <div>
        //             <span dangerouslySetInnerHTML={{__html : match.body}}></span> 
        //         </div>
        //     )
        // }

        return (
            <div>
                    videos/Articles/Tweets
            </div>
        )
    }
}
   
export default Match;
