import React from 'react';
import Moment from 'moment';
import '../../assets/css/videos/videos.css';

const CardImage = (props) => {
    const { news } = props;
    return (
        <div className="card mb-1 p-2">
            <div className='card-body p-0'>
                <div>
                    <img className='card-videos-small'
                        src={news.image}
                        alt="no Iamge" />
                </div>

                <div>
                    <div className='card-videos-small-date'>
                        <span> {news.series_name} </span>
                        <span> {Moment(new Date(news.created_at)).format("DD MMM,YYYY")} </span>
                    </div>
                    <div className='card-video-title-text' >{news.title}</div>
                    <span dangerouslySetInnerHTML={{_html: news.body}} /> 
                </div>
            </div>
        </div>
    );
}

export default CardImage;
