import React, { Component } from 'react';
import request from 'superagent';

class Videos extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            selectedType: 'live_series',
            baseUrl: 'https://back.cricwick.net/api/v1/user_api',
            telco: 'telco=mobilink',
            page: 1,
            perPage: 5
        }
    }

    componentDidMount = () => {
        this.loadSeriesRecords();
    }

    loadSeriesRecords = async () => {
        const { hasMore, page, perPage, baseUrl, telco, seriesData } = this.state;
        //https://back.cricwick.net/api/v1/user_api/get_new_series?page=1&per_page=5&telco=mobilink
        const Url = baseUrl + '/' + 'get_new_series?'
            + 'page=' + page + '&' + 'per_page=' + perPage
            + '&' + telco

        //console.log(Url);
        //console.log(hasMore);

        request
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                    hasMore: (res.length < perPage)
                });
            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    render() {

        const { error, isLoaded, seriesData } = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            //------------------------------------------------------------------- 
            return (
                <div>
                    {console.log(seriesData)}
                </div>
            );
        }
        //----------------------------------------------------------------------
    }
}

export default Videos;
