import React, { Component }         from 'react';
import LatestNews                   from './latestNews';
import Articles                     from './articles';
import {NavLink, Route,  Switch}    from 'react-router-dom';
import DetailedNews                 from './detailedNews'; 

class Stories extends Component {

    constructor(props) {
        super(props);

        const {match} =  this.props;

        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            baseUrl: 'https://back.cricwick.net/api',
            page: 1,
            perPage: 5,
            selectedType: (match.params.selectedType ? match.params.selectedType : '') ,
            id          : (match.params.id           ? match.params.id           :  0),
        }
    }

   
    //URL FOR Particular NEWS : https://back.cricwick.net/api/articles/629

   componentWillReceiveProps=(nextProps)=>{
       console.log("Component will receive Props..",nextProps);
   }


    render() {
          const {selectedType, id } = this.state;
          const { match } = this.props;
          {console.log("Stories : ", match)}  
          return (
            <div>
                {/* {console.log(match)} */}

                <div className='card card-block pl-2 pt-3 pb-3 mb-1'>
                        {/* <h6 className='menu-buttons-title-heading'>{seriesData.match.series.title}</h6> */}
                         <span className='short-menu-buttons'>
                            <NavLink className='btn btn-default' onClick={()=>this.handleClick(match.params.id)}
                                     to={`${match.url}/news`}       
                                     activeClassName="short-menu-active">Latest news </NavLink>
                            <NavLink className='btn btn-default' 
                                     to={`${match.url}/articles`}   
                                     activeClassName="short-menu-active">Articles    </NavLink>
                        </span>
                </div>

                <div id='news' style={{ cursor: 'pointer' }}
                        onClick={() => this.hideDetail()}>
                        {match.params &&
                            <DetailedNews news_id={match.params.id} selectedType={match.params.selectedType} />
                        }
                </div> 

                <div>
                      <Switch>
                          <Route exact
                              path={`${match.url}/`} render={(props) => <LatestNews {...props} />}
                          />

                           <Route exact
                              path={`${match.url}/news`}
                              render={(props) => <LatestNews {...props} />}
                          />


                          <Route
                              path={`${match.url}/articles`}
                              render={(props) => <Articles  {...props} />}
                          />

                      </Switch>

                  </div> 
            </div>
        );

    }
}

export default Stories;


