import React, { Component } from 'react';
import '../../assets/css/home/homeCenterPanel.css';
//import axios from 'axios';

class HomeCenterPanel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            homeContents : [],
            series : []
        };
    }

    componentDidMount() {

        fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        homeContents: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

   renderHome(){
       this.state.homeContents.map((item,i)=>(
            console.log(item)      
       ));
   } 

