import React, { Component } from 'react';
import config from "../../config.json";
import {NavLink, Route,  Switch}    from 'react-router-dom';
import AllVideos from './allVideos';
import Featured from './featured';
import Archives from './archives';
import '../../assets/css/videos/videos.css';

class Videos extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      isLoading: false,
      hasMore: true,
      seriesData: [],
      selectedType: '',
      baseUrl: "https://back.cricwick.net/api/v1",
      page: 1,
      perPage: 5,
      telco: config.telco,
      video: [],
      tag_id: 27,
      video_id: "",
      title: "",
      matchDesc: ""
    };
  }

  componentWillReceiveProps = async nextProps => {
    //console.log('component Will Recevie Props NextProps :', nextProps.viewable_id , nextProps.tag_id  );
    await this.setState(() => {
      return {
        selectedType: nextProps.selectedType,
        video: nextProps.video
      };
    });
    // await this.handleClick(nextProps.video)
  };

  handleClick = selectedType => {
    if (!selectedType) {
      this.setState(() => {
        return { selectedType: "All_Videos" };
      });
    } else {
      this.setState(() => {
        return { selectedType: selectedType };
      });
    }
  };

  render() {
    const {selectedType, video } = this.state;
    const {match} =  this.props; 

    return (
      

      <div>
        <div className='card card-block pl-2 pt-3 pb-3 mb-1'>
          {/* <h6 className='menu-buttons-title-heading'>{seriesData.match.series.title}</h6> */}
          <span className='short-menu-buttons'>
            <NavLink className='btn btn-default' to={`${match.url}`}             >All Videos     </NavLink>
            <NavLink className='btn btn-default' to={`${match.url}/Featured`}   >Featured       </NavLink>
            <NavLink className='btn btn-default' to={`${match.url}/Archives`}      >Archives       </NavLink>
          </span>
        </div>

        <div>
          <Switch>
            <Route exact
              path={`${match.url}`}
              render={(props) => <AllVideos {...props} selectedType="All_Videos" />}
            />
            <Route
              path={`${match.url}/featured`}
              render={(props) => <Featured {...props} selectedType="Featured" />}
            />
            <Route
              path={`${match.url}/archives`}
              render={(props) => <Archives {...props} selectedType="Archives" />}
            />
          </Switch>
        </div>

      </div>
    );
  }
  //----------------------------------------------------------------------
}

export default Videos;



