import React from 'react';
import Moment from 'moment';
import '../../assets/css/common/articles.css';

const Articles = (props) => {

  const { match } = props;
 
  return (
    <div>
      {match.map((match, i) => (
        <div key={match.id} className="flex-d">
          <div className="card mb-1 p-0">
            <div className="card-body p-3">
             
              <div className='container'>
                <div className='row'>

                  <div className='col-9 card-col-for-text'>
                    <h4 className="article-cardsmall-col-heading-black" >
                      {match.title}
                    </h4>
                    <h6 className='article-cardsmall-col-sub-heading-gray'>
                      {Moment(new Date(match.created_at)).format("DD MMM, YYYY")}
                    </h6>
                  </div>
                </div>

                <div className='col-3 card-col-for-image'>
                  <img
                    className="article-card-image-small"
                    src={match.image}
                    alt="no Iamge"
                  />
                </div>

              </div>
            </div>

          </div>
        </div>
      ))}
    </div>
  );
}

export default Articles;
