import React, { Component } from 'react';   
import http from 'superagent';
import '../../assets/css/summary/summary.css';

//https://back.cricwick.net/api/v3/innings/5087/get_previous_overs?over_number=42

class MatchBallByBall  extends Component {

    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            over: null,
            selectedType: 'summary',
            baseUrl: 'https://back.cricwick.net/api',
        };

    }

    componentDidMount() {
        this.loadSeriesRecords();
        // this.state.seriesData['live_series'].map((item,i)=>(
        //       console.log(item)
        // ));
        //console.log( "From Component Did Mount : " , this.state.seriesData);
    }

    loadSeriesRecords = async () => {
        const { baseUrl } = this.state;
        const { inning, overNumber } = this.props;

        // console.log("Match from Parent Object : " , match);
        // fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
        //Home Series Api  

        const Url = baseUrl + '/'
            + 'v3'
            + '/'
            + 'innings'
            + '/'
            + inning.id
            + '/'
            + 'get_previous_overs'
            + '?'
            + 'over_number' + overNumber 

        console.log(Url);
        //console.log(hasMore);

        http
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                });
            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    render() { 
        return ( 
            <div> 
                 <h6> Match Ball By Ball  </h6>
            </div> 
         );
    }
}
 
export default MatchBallByBall;
