import React, { Component } from 'react';
import '../../assets/css/home/homeCenterPanel.css';
//import axios from 'axios';

class HomeCenterPanel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            homeContents : []
        };
    }

    componentDidMount() {

        fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        homeContents: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    render() {

        const { error, isLoaded, homeContents } = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            
            console.log(homeContents);

            return (
                <div>
                   
                    <div>
                        {homeContents.map(home => (
                            <div> {home.title} <br/>
                               {/* {home.data.map(type=>(
                                    {type.data[0].title}
                                ))}   
                                    */}
                          </div>
                        ))}
                    </div>

                   {/* {homeContents[0].data[2].data[0].title}, <br/>
                   {homeContents[0].data[2].data[0].teamA.short_name} <br/>
                    
                   {homeContents[0].data[2].data[0].teamB.short_name} <br/>
                   {homeContents[0].data[2].data[0].teamB.short_name} <br/>

                   {homeContents[0].data[2].data[0].detail} */}

                    {/* {homeContents.map(match=>(
                           <h6 key={match[0]}> 
                                 {match.type} 
                           </h6>
                    ))} */}

                    <div className="card">
                        <div className="card-header">
                            <h6 > {homeContents[0].title} </h6>
                        </div>
                         <div className='card-text'>
                            
                        </div>
                    </div>
                </div>
           
            );
        }
    }
}


export default HomeCenterPanel;


