import React, { Component } from 'react';   
import http from 'superagent';

class MatchSquads  extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            over: null,
            selectedType: 'summary',
            baseUrl: 'https://back.cricwick.net/api',
        };

    }

   

    componentDidMount() {
        this.loadSeriesRecords();
    }

    loadSeriesRecords = async () => {
        const { baseUrl } = this.state;
        const { match } = this.props;

        // console.log("Match from Parent Object : " , match);
        // https://back.cricwick.net/api/1107/2409/squad
        //Home Series Api  

        const Url = baseUrl + '/'
            + 'v3'
            + '/'
            + 'matches'
            + '/'
            + match.id
            + '/'
            + 'summary'

        console.log(Url);
        //console.log(hasMore);

        http
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                });
            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }


    render() {
        const {match} =  this.props;
        console.log(match.id);
        console.log(match.team_1_id,match.teamA.name);
        console.log(match.team_2_id, match.teamB.name);


        return ( 
            <div> 
                 <h6> Match Squads </h6>
           </div> 
        );
    }
}
 
export default MatchSquads;
