import React , {Component} from 'react';
import '../../assets/css/home/homeCenterPanel.css';
class HomeCenterPanel extends Component{
    
    constructor(props){
        super(props);
        this.stete={
            hasError : null,
            isLoaded : false,  
            liveMatches:[]
         }   
    }

    componentDidMount() {

        fetch("http://54.245.34.7:8080/api/v2/main/live_matches")
            .then(res => res.json())
            .then((result) => {
                this.setState({ liveMatches: result });
            }, (hasError) => {
                this.setState({ isLoaded: true, hasError: true});
            });
    }

    render(){

        const{ hasError, isLoaded, liveMatches } =  this.state;
        if (hasError) {
            return <div> Error : {hasError.message}</div>
        } else if (!isLoaded) {
            return <div> Loading.... </div>
        }         
         return(
             <div>
                {liveMatches.map(match =>(
                        {match}
                ))} 

