import React, { Component } from 'react';
//import { render } from 'react-dom';
import request from 'superagent';

import '../../assets/css/home/homeCenterPanel.css';
import SeriesContentsIdentifire from './seriesContentsIdentifire';
import OtherContentsIdentifire from './otherContentsIdentifire';
//import axios from 'axios';

class HomeCenterPanel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            homeContents: [],
            tempContents: [],
            view: 'home',
            webuser: 1,
            page: 1,
            perPage: 5,
            ranking: [],
            series: [],
            match: null,
            baseUrl: 'http://back.cricwick.net/api/v3/view_lists'
        };
    }

    loadSeriesRecord = () => {

        // fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
        //Home Series Api  
        const Url = this.state.baseUrl + '/'
            + 'get_by_name?view=' + this.state.view
            + '&web_user=' + this.state.webuser
            + '&page=' + this.state.page
            + '&per_page=' + this.state.perPage;

        request
            .get(Url)
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    homeContents: [...this.state.homeContents, ...res.body],
                    hasMore: (res.length < this.state.perPage)
                });
            })
            .catch(error => {
                this.setState({
                    inLoaded: true,
                    error
                });
           });
    }


    componentDidMount() {
        window.addEventListener('scroll', this.handleScroll);
        this.loadSeriesRecord();

    }

    componentWillUnmount() {
        window.removeEventListener('scroll', this.handleScroll);
    }


    handleScroll = () => {
        // console.log("inner Height : ", window.innerHeight);
        // console.log("scrollTop Height : ",document.documentElement.scrollTop);
        //console.log("Offset Height : ", document.documentElement.offsetHeight);

        let scrollHeight = (window.innerHeight + document.documentElement.scrollTop);
        let offsetHeight = document.documentElement.offsetHeight;

        console.log("scrollTop Height : ", scrollHeight);
        console.log("offset    Height : ", offsetHeight);


        if (parseInt(scrollHeight) === parseInt(offsetHeight)) {
            this.setState({ page: this.state.page + 1 });
            this.loadSeriesRecord();
            console.log("Loading page ...# : ", this.state.page);
            console.log("Loading page ...# : ", this.state.homeContents);
        }

    }

    render() {
        const { error, isLoaded, homeContents } = this.state;

        //let tempContents1 =  homeContents.filter(x => x.type !== 'generic-home');
        //let genericHome =  homeContents.filter(x => x.type === 'generic-home');

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {

            return (

                <div onScroll={this.handleScroll}>

                    {/* Itreate top level contents line Series and Other Data */}
                    {homeContents.map((series, cnt) => (
                        <div key={cnt}>

                            <div className="home-div">

                                {/* Check Either the Current Level of Itration is a ( SERIES) */}
                                {series.type === 'series' &&
                                    series.data.map((matches, mat) => (

                                        <div key={mat} >
                                            {/* Render Series Contents Matches/Videos/Articles/Tweets etc */}
                                            {matches.data.map((match, i) => (
                                                <div key={i}>
                                                    <SeriesContentsIdentifire match={match}
                                                        printSeriesTitle={i}
                                                        seriesTitle={series.title}
                                                        contentType={matches.type} />
                                                </div>
                                            ))}
                                            {/* ----------------- End Series Contents ------------- */}
                                        </div>
                                    ))}
                                {/* -------------------------------------------------------------------------- */}

                                {/* Check Either the Current Level of Itration is a ( RANKING ) */}

                                {/* {series.type==='ranking' && 
                                        <div>
                                             {this.setState({ranking: series})}
                                        </div>  
                                 }  */}

                                {/* --------------------------------------------------------------------------

                                {/* Check Either the Current Level of Itration is Other than  ( VIDEO ) */}
                                {series.type === 'video' &&
                                    console.log(series)
                                }
                                {/* ------------------------------------------------------------------------- */}



                                {/* Check Either the Current Level of Itration is Other than  ( SERIES ) */}
                                {series.type === 'generic-home' &&
                                    <div>
                                        <OtherContentsIdentifire contents={series}
                                            contentType={series.type}
                                        />

                                    </div>
                                }
                                {/* ------------------------------------------------------------------------- */}

                            </div>
                        </div>

                    ))}
                    {/* -------------END OF ITRATION--------------------------- */}

                </div>
            )

        }
    }
}

export default HomeCenterPanel;
