import React, { Component } from 'react';
import http from 'superagent';

class MatchSquads extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            team_id : '',
            match_id:'',
            hasMore: true,
            seriesData: [],
            over: null,
            selectedType: 'summary',
            baseUrl: 'https://back.cricwick.net/api',
        };

    }


    componentDidMount() {
        const { match } = this.props;
        this.loadSeriesRecords(match.team_1_id, match.id);
    }

    loadSeriesRecords = async (team_id, match_id) => {
        const { baseUrl } = this.state;


        // console.log("Match from Parent Object : " , match);
        // https://back.cricwick.net/api/1107/2409/squad
        //Home Series Api  

        const Url = baseUrl
            + '/'
            + team_id
            + '/'
            + match_id
            + '/'
            + 'squad'

        console.log(Url);

        http
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                });
            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    handleClick = (team_id, match_id) => {
          this.setState({ team_id: team_id, match_id: match_id });
          this.loadSeriesRecords(team_id, match_id);
    }

   
    renderSquad = (ObjectSelectedSquad) => (
        <div>
          {ObjectSelectedSquad.map((player, i)=>(
               console.log(player)
          ))}
        </div>
    )


    render() {
        // console.log(match.id);
        // console.log(match.team_1_id,match.teamA.name);
        // console.log(match.team_2_id, match.teamB.name);
        const { error, isLoaded, seriesData} = this.state;
        const {match} = this.props;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            return (
                <div>
                   <div>
                        <span>
                            <button className='btn link-button btn-link mr-2 p-0 btn-sm' onClick={() => this.handleClick(match.team_1_id, match.id)} >{match.teamA.name}</button>
                            <button className='btn link-button btn-link mr-2 p-0 btn-sm' onClick={() => this.handleClick(match.team_2_id, match.id)}>{match.teamB.name}</button>
                        </span>
                     
                    </div>

                    <div>
                        {this.renderSquad(seriesData)}
                    </div>

                </div>
            );
        }
    }
}

export default MatchSquads;
