import React from 'react';
import Moment from 'moment';

const Articles = (props) => {

  const { match } = props;

  return (
    <div>
      {console.log(match)}
      {match.map((article, i) => (
        <div key={article.id} className="flex-d">
          <div className="card mb-1 p-0">
            <div className='card-body p-3'>
              <div>
                <img className='home-center-series-article-videos-small'
                  src={match.image}
                  alt="no Iamge" />
              </div>

              <div className='home-center-series-article-videos-small-text' >
                <h6>
                  {match.title}
                </h6>

                <h6 style={{ fontWeight: "normal" }} >
                  {Moment(new Date(match.created_at)).format("DD MMM, YYYY")}
                </h6>
              </div>

            </div>
          </div>
        </div>
      ))}
    </div>
  )
}

export default Articles;
