import React, { Component } from 'react';
import http from "../../services/httpService";
//import {userAuthentication}  from "../../services/userService";
import * as authService from "../../services/authService";
import Input from "../common/input";
import config from '../../config.json';


class Login extends Component {
    constructor(props) {
        super()
        this.state = {
            errors: null,
            isLoaded: false,
            isLoading: false,
            baseUrl: 'https://staging.cricwick.net:13002/api',
            page: 1,
            perPage: 10,
            user: { phone: "", userid: "", username: "" }
        }

        this.handleSumbit = this.handleSumbit.bind(this);
        this.handleChange = this.handleChange.bind(this);

    }

    componentDidMount() {
        // this.loadSeriesRecords();
        //  console.log("url : ", config.telco_app_load);
    }

    loadSeriesRecords = async () => {
        const { user } = this.state;

        //login_or_sendpin?telco=mobilink&phone=03001111111&sub_type='

        const Url = this.state.baseUrl + '/'
            + 'login_or_sendpin?telco=' + config.telco
            + '&phone=' + user.username
            + '&sub_type='

        console.log("Series : ", Url);

        try {
            const data = await http.get(Url);
            await this.setState(() => {
                return {
                    user: data.data
                }
            });

            console.log("Status Pushed", data.data);
            console.log("user", data.data.generic_response);
            localStorage.push("user", data.data.generic_response);

        }
        catch (err) {
            console.log("Error", err);
            window.location.href = '/404notfound';
        };
    }

    //jwt-decode
    //import jwtDecode from 'jwt-decode'
    handleSumbit = async () => {
        const { user } = this.state;
        console.log("Submit Clicked...: ", user);
        //await this.loadSeriesRecords(user.username);
        // this.props.history.push('/');
        e.preventDefault();
    }

    handleChange = ({ currentTarget: input }) => {
        const user = { ...this.state.user };
        user[input.name] = input.value;
        this.setState({
            user: user
        })
    }

    render() {
        const { user } = this.state;
        return (
            <div className='card p-4'>
                <form onSubmit={this.handleSubmit}>
                    <Input name='username'
                            id='username'
                            value={user.username}
                            onChange = {this.handleChange}
                            label="Please Enter Your Number"
                            placeHolder="03xx xxxx xxx"
                            focus={true}
                     />
                  
                    <button
                        className="btn btn-primary mt-3"
                        value="submit"
                        // onClick = {(event) => this.handleSumbit(event.target.value)}
                        type="submit">Continue
                        </button>
                </form>

                <div style={{ paddingTop: 30 + 'px' }} >
                    <img className="card-img-bottom"
                        //  src={config.telco_app_load} alt='NoImage'/>
                        src={require('../../assets/telcos/img-download.jpg')} alt='NoImage' />
                </div>

            </div>
        );
    }
}

export default Login;

