import React, { Component } from 'react';
import MatchSummary         from './matchSummary';
import MatchVideos          from './matchVideos';
import MatchBallByBall      from './matchBallByBall';
import MatchSquads          from './matchSquads'; 
import ScoreCard            from './scoreCard';
import Match                from 'mathjs';

class Summary extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            over: null,
            selectedType: 'summary',
            baseUrl: 'https://back.cricwick.net/api',
        };

    }


    handleClick = async (selectedType) => {

        if (!selectedType) {
            await this.setState({ selectedType: 'summary' });
        } else {
            await this.setState({ selectedType: selectedType });
        }

    }


    handleScroll = () => {
        const {hasMore} =  this.state;
        const offset = (document.documentElement.scrollTop + window.innerHeight);
        const height = document.documentElement.offsetHeight;
 
 
         console.log("Scrolling"); 

         
 
         if (!hasMore){
             //console.log("offset : ", Math.ceil(offset));
             //console.log("height : ", height);
             if (Match.ceil(offset) === height) {
                 //this.setState({ page: this.state.page + 1 });
                 //this.loadSeriesRecord();
             }
         }else{
             console.log('End of list....');
         }
     }
 


    render() {
        const {selectedType} = this.state;
        const {match} = this.props.location.state;
       
            return (
                <div onScroll={this.handleScroll}>
                    <div className='card card-block mb-1'>
                        <span>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('summary')} >Summary</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('score-card')}>Scorecard</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('videos')}>Videos</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('ball_by_ball')}>Ball by Ball</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('squads')}>Squads</button>
                        </span>
                    </div>
                    

                   {selectedType === 'summary' &&
                        <div className='card p-3 mb-1'>
                            <MatchSummary match={match} />
                        </div>
                    }
                          
                    {selectedType === 'score-card' &&
                        <div className='card p-3 mb-1'>
                            <ScoreCard match={match}/>
                        </div>
                    }

                    {selectedType === 'videos' &&
                        <div className='card p-3 mb-1'>
                            <MatchVideos />
                        </div>
                    }

                    {selectedType === 'ball_by_ball' &&
                        <div className='card p-3 mb-1'>
                            <MatchBallByBall match={match}/>
                        </div>
                    }       
                 
                    {selectedType === 'squads' &&
                        <div className='card p-3 mb-1'  >
                             <MatchSquads match={match} {...this.props}  />
                        </div>
                    }       



                  </div>
            );
        }
    }

export default Summary;
