import React, { Component } from 'react';   
import '../../assets/css/home/homeCenterPanel.css';

class SeriesHomeCenter extends Component {
    constructor(props) {
        super(props);
        this.state = { 
             homeSeries : [],
             renderTag : ''
         }
    }

    renderInnings = (series) => {
        let tagText = '';
                 
         if( series.type === 'matches'){
             series.data.map(item => {
                tagText = <div> {item.teamA.short_name} </div>
                return tagText
             });
         }

         if(series.type==='videos'){
           // console.log("Videos");
           // tagText= "this is Videos Object";
         }
         
         if(series.type==='news'){
            //console.log("news");
         }
        
         if(series.type==='articles'){
            //console.log("articles");
         }


    }

    render() {
        const { homeSeries, renderTag  } = this.props;
        //let series = homeContents.filter(x => x.type === 'series');
        //console.log(homeSeries[0]);
        return (
            <div> 

                {homeSeries.map((item, i)=>(
                         <div className="card" key={i}>
                          <h5 className="card-title">{item.title}</h5>
                           <div className="card-body"> 
                                   {item.data.map((series, i)=>(
                                       <div key={i}> {renderTag} </div>
                                    ))}    
                                 {/* <img className='flag-small-image' 
                                      src={item.data[0].data.teamA.flag_url} 
                                      alt="no image"/>
                                 {item.data[0].data.teamA.short_name} <br/>
                                 {this.renderInnings(item.data[0].data.innings,item.data[0].data.team_1_id)} <br/>
                                                                   
         
                                 <img className='flag-small-image' 
                                      src={item.data[0].data.teamB.flag_url} 
                                      alt="no image"/>
                                 {item.data[0].data.teamB.short_name} */}


                           </div>
                      </div>  
                ))}  
            </div>
        )
    }
}

 
export default SeriesHomeCenter;

