import React, { Component } from 'react';
import config from "../../config.json";
import request from 'superagent';
import AllVideos from './allVideos';
import Featured from './featured';
import Archives from './archives';
import '../../assets/css/videos/videos.css';

class Videos extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            selectedType: this.props.location.state.selectedType,
            baseUrl: 'https://back.cricwick.net/api/v1',
            page: 1,
            perPage: 5,
            telco: config.telco,
            video: [],
            tag_id: 27,
            video_id: '',
            title: '',
            matchDesc: ''
        };

    }

    componentWillReceiveProps = async (nextProps) => {
        //console.log('component Will Recevie Props NextProps :', nextProps.viewable_id , nextProps.tag_id  );
        await this.setState({
          selectedType: nextProps.selectedType,   
          video: nextProps.video
        });

       console.Console("Props from Videos : " ,nextProps); 
        // await this.handleClick(nextProps.video)
    }


    loadSeriesRecords = async (selectedType) => {
        const { hasMore, page, perPage, baseUrl, telco } = this.state;
        //https://back.cricwick.net/api/v1/user_api/get_new_series?page=1&per_page=5&telco=mobilink

        let Url = '';


        //3) Arcived:
        //   Base URL =  https://back.cricwick.net/api/v1
        //   Composed =  /video/get_by_video_type/archive/1/5?telco=mobilink

        if (selectedType === "Archives") {
            Url = `${baseUrl}/video/get_by_video_type/archive/${page}/${perPage}?${telco}`
        }

        console.log(hasMore, Url);
        await request
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                    hasMore: (res.length < perPage)
                })

            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    handleClick = (selectedType) => {
        if (!selectedType) {
            this.setState({ selectedType: 'All_Videos' }, () => {
                console.log(this.state.selectedType);
            })
        } else {
            this.setState({ selectedType: selectedType }, () => {
                console.log(this.state.selectedType);
            })
        }

    }

    render() {
        const { selectedType } = this.state;
        return (
            <div>
                <div className='card card-block mb-1'>
                    <span>
                        <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('All_Videos')}>All Videos</button>
                        <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('Featured')}>Featured</button>
                        <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('Archives')}>Archives</button>
                    </span>
                </div>

                <div>
                    {selectedType === 'All_Videos' &&
                        <AllVideos selectedType={selectedType} />
                    }
                </div>

                <div>
                    {selectedType === 'Featured' &&
                        <Featured selectedType={selectedType} />
                    }
                </div>

                <div>
                    {selectedType === 'Archives' &&
                        <Archives selectedType={selectedType} />
                    }
                </div>

            </div>
        );
    }
    //----------------------------------------------------------------------
}

export default Videos;
