import React, { Component } from 'react';
import '../../assets/css/home/homeCenterPanel.css';
import SeriesHomeCenter from './seriesHomeCenter';
//import axios from 'axios';

class HomeCenterPanel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            homeContents : [],
            series : []
        };
    }

    componentDidMount() {
         
        // fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
        //Home Series Api  
        fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home&web_user=1&page=1")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        homeContents: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    renderTag=(objects, type)=>{

             
               

    }


    render() {
        const { error, isLoaded, homeContents } = this.state;
        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
          
        return(
          <div>
                {homeContents.map((item, i) => (
                    <div key={i}>
                        {item.title}
                        <div>
                            {item.data.map((item, i) => (
                              <div key={i}>  
                                   {this.renderTag(item, item.type)}
                              </div>   
                            ))}
                        </div>
                    </div>     
              ))}
          </div> 
        )                 
      }
    }
}

export default HomeCenterPanel;
