import React, { Component } from 'react';
import {NavLink, Route}    from 'react-router-dom';

import MatchSummary         from './matchSummary';
import MatchVideos          from './matchVideos';
import MatchBallByBall      from './matchBallByBall';
import MatchSquads          from './matchSquads'; 
import ScoreCard            from './scoreCard';

class Summary extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            over: null,
            match_id:0,  
            selectedType: 'summary',
            baseUrl: 'https://back.cricwick.net/api',
        };

    }

    componentDidMount= async () =>{
           const {match, selectedType} =  this.porps
           await this.setState(()=>{ return{ match_id : match.params.match_id ,selectedType: selectedType }});

    }

    handleClick = async (selectedType) => {

        if (!selectedType) {
            await this.setState(()=>{ return{selectedType: 'summary' }});
        } else {
            await this.setState(()=>{ return{selectedType: selectedType }});
        }

    }

    render() {
        const {selectedType, match_id} = this.state;
        const {match} =  this.props;

        console.log(this.props);
       
            return (
                <div id='match_summary'>
                    <div className='card card-block pl-2 pt-3 pb-3 mb-1'>
                        {/* <h6 className='menu-buttons-title-heading'>{match.series.title}</h6>  */}
                         <span className='short-menu-buttons'>
                            <NavLink className='btn btn-default' to={`${match.url}/summary`}     >Summary      </NavLink>
                            <NavLink className='btn btn-default' to={`${match.url}/scorecard`}   >Scorecard    </NavLink>
                            <NavLink className='btn btn-default' to={`${match.url}/videos`}      >Videos       </NavLink>
                            <NavLink className='btn btn-default' to={`${match.url}/ballbyball`}  >Ball by Ball </NavLink>
                            <NavLink className='btn btn-default' to={`${match.url}/squads`}      >Squads      </NavLink>
                        </span>

                    </div>

                    <div>
                        <Route exact
                            path={`${match.url}/summary`}
                            render={(props) => <MatchSummary {...props} match_id= {match_id}  selectedType="summary" />}
                        />

                        <Route strictly exact
                            path={`${match.url}/scorecard`}
                            render={(props) => <ScoreCard {...props} viewable_type={viewable_type} viewable_id={viewable_id} selectedType="scorecard" />}
                        />
                        <Route strictly exact
                            path={`${match.url}/videos`}
                            render={(props) => <MatchVideos {...props} viewable_type={viewable_type} viewable_id={viewable_id} selectedType="videos" />}
                        />
                        <Route strictly exact
                            path={`${match.url}/ballbyball`}
                            render={(props) => <MatchBallByBall {...props} viewable_type={viewable_type} viewable_id={viewable_id} selectedType="ballbyball" />}
                        />
                        <Route strictly exact
                            path={`${match.url}/squads`}
                            render={(props) => <MatchSquads {...props} viewable_type={viewable_type} viewable_id={viewable_id} selectedType="ballbyball" />}
                        />
                    </div>  

                  </div>
            );
        }
    }

export default Summary;



