import React, { Component } from 'react';
import request from 'superagent';
import Moment from 'moment';
import DetailedNews from './detailedNews';
//import '../../assets/css/videos/videos.css';
import '../../assets/css/common/videoCards.css'


class Articles extends Component {
  

    constructor(props) {
        super(props)
        this.state = {
            error     : null,
            isLoaded  : false,
            isLoading : false,
            hasMore   : true,
            seriesData: [],
            baseUrl   : 'https://back.cricwick.net/api',
            page      : 1,
            perPage   : 5,
            type      : '',
            article     : props.objSelectedStory,
            showFull  : props.showFull
        }
    }


    componentDidMount = async () => {
        await this.loadSeriesRecords();
    }


    componentWillReceiveProps = async (nextProps) =>{
       
        // console.log( "Component Will Recevie props - selectedType : "     , nextProps.selectedType);
        // console.log( "Component Will Recevie props - objSelectedStory : " ,nextProps.objSelectedStory);
        // console.log( "Component Will Recevie props - showFull   : "      ,nextProps.showFull);

             await this.setState({
                  article          :  nextProps.objSelectedStory,
                  showFull         :  nextProps.showFull,
             });
   }



    loadSeriesRecords = async () => {
        const { page, perPage, baseUrl } = this.state;

        // 1) All Videos :
        //    Base URL =   https://back.cricwick.net/api 
        //    Composed =  /articles?page=1&page_size=6


        let Url = baseUrl
            + '/'
            + 'articles'
            + '?'
            + 'page=' + page
            + '&'
            + 'page_size='
            + perPage

        console.log(Url);

        await request
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                    hasMore: (res.body.length < perPage)
                });

            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    handleClikc = (article) => {
        this.setState({
            showFull: true, article
        });
    }

    hideDetail = () => {
        this.setState({
            showFull: !this.state.showFull
        });
    }



    render() {
        const { isLoaded, error, seriesData, showFull, article } = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            return (
                <div>
                    <div hidden={!showFull} onClick={() => this.hideDetail()}>
                        <DetailedNews news={article} />
                    </div>
                    {seriesData.data.map((article, i) => (
                        <div className="flex-d" key={article.id} >
                            <div className="card mb-1 p-2" onClick={() => this.handleClikc(article)} style={{ cursor: 'pointer' }}>
                                <div className='card-body p-0'>
                                    
                                    <div className='container' >
                                        <div className='row'>
                                            <div className='col-3 card-col-for-image'>
                                                <img className='card-image-small'
                                                    src={article.image}
                                                    alt="no Iamge" />
                                            </div>

                                            <div className='col-9 card-col-for-text'>
                                                <h4 className='cardsmall-col-sub-heading-gray'>
                                                    {article.series_name}
                                                </h4>

                                                <h4 className='cardsmall-col-sub-heading-gray'>
                                                    {Moment(new Date(article.created_at)).format("DD MMM,YYYY")}
                                                </h4>

                                                <h6 className='card-video-title-text' >
                                                    {article.title}
                                                </h6>
                                                <p className='cardsmall-col-bodytext-black'>
                                                    {article.body.substr(5, 200)}
                                                </p>

                                            </div>
                                        </div>

                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            );
        }
    }
}

export default Articles;
