import React, { Component } from 'react';
import '../../assets/css/home/homeRankingICC.css';

class RankingContentsIdentifire extends Component {
    constructor(props) {
        super(props);
        this.state = {
            matchType: 'odi',
            matchTypeToDisplay: 'ODI',

        };
    }

    handleClick = (rankingType) => {

        if (rankingType === 'odi') {
            this.setState({ matchType: 'odi', matchTypeToDisplay: 'ODI' });
        }

        if (rankingType === 't20') {
            this.setState({ matchType: 't20', matchTypeToDisplay: 'T20' });
        }

        if (rankingType === 'test_') {
            this.setState({ matchType: 'test_', matchTypeToDisplay: 'TEST' });
        }

    }

    renderRanking = (data) => {
        return (
            <div>
                {data.map((player, i) => (
                    <div key={i}>
                        <div key={i} className="card mycard p-2 text-center">
                            <div className="card-title">
                                <img
                                    className="img"
                                    src={player.player_display_picture}
                                    alt="noImage"
                                />
                            </div>

                            <div className="card-title">
                                <span className="player-rating-number">{player.rating}</span>{" "}
                                <br />
                                <span>Rating</span>

                                <br /> <br />
                                <span className='home-ranking-player-name'> {i + 1} {" - "} {player.player_name} </span>
                                <br />
                                {player.team_name}

                            </div>
                        </div>
                    </div>
                ))}
            </div>
        );
    };

    render() {

        const { contents } = this.props;
        const { matchType, matchTypeToDisplay } = this.state;

        console.log(contents);

        if (!contents.data) return "";

        return (
            <div>

                <div>
                    <span>
                        <button className='btn mybutton btn-default mr-2 btn-sm' onClick={() => this.handleClick('odi')} >ODI</button>
                        <button className='btn mybutton btn-default mr-2 btn-sm' onClick={() => this.handleClick('t20')}>T20</button>
                        <button className='btn mybutton btn-default mr-2 btn-sm' onClick={() => this.handleClick('test_')}>TEST</button>
                    </span>
                </div>

                {/* <!-- This  following Code is for  ODI Ranking  --> */}
                {matchType === 'odi' &&

                    <div className="card p-3">
                        <h6 className="card-title home-ranking-main-title">
                            ICC RANKING -
                                   <span className='home-ranking-main-title-dynamic'>
                                {' '} {matchTypeToDisplay}
                            </span>
                        </h6>

                        <h6 className="card-title home-ranking-sub-title">ALL ROUNDERS</h6>
                        <div className="card-columns">
                            {this.renderRanking(contents.data.odi.all_rounder)}
                        </div>

                        <h6 className="card-title home-ranking-sub-title">BOWLERS</h6>
                        <div className="card-columns">
                            {this.renderRanking(contents.data.odi.bowler)}

                        </div>

                        <h6 className="card-title home-ranking-sub-title">BATSMEN</h6>

                        <div className="card-columns">
                            {this.renderRanking(contents.data.odi.batsmen)}

                        </div>
                    </div>
                }

                {/* <!-- This  following Code is for  T20 Ranking  --> */}
                {matchType === 't20' &&

                    <div className="card p-3 mb-2">
                        <h6 className="card-title home-ranking-main-title">
                            ICC RANKING -
                             <span className='home-ranking-main-title-dynamic'>
                                {' '} {matchTypeToDisplay}
                            </span>
                        </h6>

                        <h6 className="card-title home-ranking-sub-title">ALL ROUNDERS</h6>
                        <div className="card-columns">
                            {this.renderRanking(contents.data.t20.all_rounder)}
                        </div>

                        <h6 className="card-title home-ranking-sub-title">BOWLERS</h6>
                        <div className="card-columns">
                            {this.renderRanking(contents.data.t20.bowler)}

                        </div>

                        <h6 className="card-title home-ranking-sub-title">BATSMEN</h6>

                        <div className="card-columns">
                            {this.renderRanking(contents.data.t20.batsmen)}
                        </div>
                    </div>
                }

                {/* <!-- This  following Code is for TEST Ranking  --> */}
                {matchType === 'test_' &&

                    <div className="card p-3 mb-2">
                        <h6 className="card-title home-ranking-main-title">
                            ICC RANKING -
                                   <span className='home-ranking-main-title-dynamic'>
                                {' '} {matchTypeToDisplay}
                            </span>
                        </h6>

                        <h6 className="card-title home-ranking-sub-title">ALL ROUNDERS</h6>
                        <div className="card-columns">
                            {this.renderRanking(contents.data.test_.all_rounder)}
                        </div>

                        <h6 className="card-title home-ranking-sub-title">BOWLERS</h6>
                        <div className="card-columns">
                            {this.renderRanking(contents.data.test_.bowler)}

                        </div>

                        <h6 className="card-title home-ranking-sub-title">BATSMEN</h6>

                        <div className="card-columns">
                            {this.renderRanking(contents.data.test_.batsmen)}

                        </div>
                    </div>
                }



            </div>
        );
    }
}

export default RankingContentsIdentifire;
