import React, { Component } from 'react';
import '../../assets/css/home/homeCenterPanel.css';
//import Match from './match';
//import axios from 'axios';

class HomeCenterPanel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            homeContents: [],
            tempContents: [],
            series: [],
        };
    }

    componentDidMount() {

        // fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
        //Home Series Api  
        fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home&web_user=1&page=1")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        homeContents: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }


    renderMatchTeamATag(match, id){
        //let teamA_Id =  match.team_1_id
        let tagText ='';
        
        //let scoreI1 = '';
        //let scoreI2 = '';

       if(!match.innings)
           return tagText='';   

        match.innings.map((score,i)=>{
           console.log(score)    
        })
        
        // if(match.innings.length > 0) {
        //   tagText = 
        //   <span> 
        //       {match.innings[0].runs}{ " "}/{ " "}
        //       {match.innings[0].wickets} {" "} 
        //       ({match.innings[0].overs}) 
        //   </span>
        // }

        return tagText;
    }

    renderMatchTeamBTag(match,id){
        let tagText = '';
       // let teamB_Id =  match.team_2_id
      
       if(!match.innings)
           return tagText='';   
       
        match.innings.map((score,i)=>{
            console.log(score)    
         })
         
        // if(!match.innings[1]){
        //    console.log(match.innings[1])
        //    tagText =  <span></span>
        //    return 
        // }   

        // if (match.innings.length >= 1) {
        //     tagText =
        //         <span>
        //             {match.innings[1].runs}{" "}/{" "}
        //             {match.innings[1].wickets} {" "}
        //             ({match.innings[1].overs})
        //         </span>
          
        // }
        return tagText;
   }



    renderMatches(matches) {
        return (
            matches.data.map((match, i) => (
                <div className="card card-block mb-1" key={match.id}>
                    {/* Following Code Render Flags or team A */}
                    <img className='header-flag-image' style={{ width: 25 + "px", height: 25 + "px" }}
                        src={match.teamA.flag_url} alt="no Iamge" />

                    <span className="header-text-teams-shortname">
                        {match.teamA.short_name}
                    </span>

                    <span className="header-text-teama-score">
                        {this.renderMatchTeamBTag(match)}
                    </span>


                    {/* Following Code Render Flags or team B */}
                    <img className='header-flag-image' style={{ width: 25 + "px", height: 25 + "px" }}
                        src={match.teamB.flag_url} alt="no Iamge" />
                    <span className="header-text-teams-shortname">
                        {match.teamB.short_name}
                    </span>

                    {/* Following Code Render Score team B */}
                    <span className="header-text-teama-score">
                        {this.renderMatchTeamATag(match)}
                    </span>
                </div>
            ))
        )
    }
 
    render() {
        const { error, isLoaded, homeContents } = this.state;
        var tagText = '';
        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
         
            homeContents.map((series,i)=>{
                if(series.type==='series'){
                    series.data.map((matches,i)=>{
                         if(matches.type==='matches'){
                            tagText = this.renderMatches(matches);
                         }
                         return '0';
                    })
                    return '0';
                }
                else{
                    console.log(series.type);    
                }
            });
            
              return <div>{tagText}</div>;
       }
    }
}

export default HomeCenterPanel;

