        //            type  : type,
        //         showFull : showFull}, 
        // ()=>{
        //        console.log("State will update in this function...", this.state)    
            
        //  }); 


    }

    componentWillReceiveProps = (nextProps) =>{
         console.log( "Component Will Recevie Props : " ,nextProps.selectedType);
    }


    handleClick = (selectedType) => {
        this.setState({ selectedType: selectedType, showFull:false });
    }

    render() {

        const { selectedType} = this.state;
        
          return (
            <div>
                <div className='card'>
                    <span>
                        <button className='btn btn-link' onClick={() => this.handleClick('Latest_News')}>Latest News</button>
                        <button className='btn btn-link' onClick={() => this.handleClick('Articles')}>Articles</button>
                    </span>
                </div>

                {selectedType === 'Latest_News' &&
                    <div>
                        <LatestNews />   
                        
                    </div>
                }

                {selectedType === 'Articles' &&
                    <div>
                        <Articles/>
                    </div>
                }

            </div>
        );

    }
}

export default Stories;
