import React, { Component } from 'react';   
import '../../assets/css/home/homeCenterPanel.css';

class SeriesHomeCenter extends Component {
    constructor(props) {
        super(props);
        this.state = { 
             homeSeries : [],
             teamA : '',
             teamB :''
         }
    }

    renderInnings = (innings, team_id) => {
        let tagText = '';
        console.log("team-id :", team_id)
        innings.map((inning, i) => {
            if (inning.batting_team_id === team_id)
                console.log(inning.runs);
                tagText += inning.runs + " / ";
                return tagText;
        });

        return tagText;

    }

    render() {
        const { homeSeries } = this.props;
        //let series = homeContents.filter(x => x.type === 'series');
        //console.log(homeSeries[0]);
        return (
            <div> 

                {homeSeries.map((item, i)=>(
                         <div className="card" key={i}>
                          <h5 className="card-title">{item.title}</h5>
                           <div className="card-body"> 
                                   {item}
                                   {item.data.map((match, i)=>(
                                        <div key={i}> {match.type} </div>
                                    ))}    
                                 {/* <img className='flag-small-image' 
                                      src={item.data[0].data.teamA.flag_url} 
                                      alt="no image"/>
                                 {item.data[0].data.teamA.short_name} <br/>
                                 {this.renderInnings(item.data[0].data.innings,item.data[0].data.team_1_id)} <br/>
                                  
                                   
         
                                 <img className='flag-small-image' 
                                      src={item.data[0].data.teamB.flag_url} 
                                      alt="no image"/>
                                 {item.data[0].data.teamB.short_name} */}


                           </div>
                      </div>  
                ))}  
            </div>
        )
    }
}

 
export default SeriesHomeCenter;

