import React, { Component } from 'react';
import request              from 'superagent';
import '../../assets/css/videos/videos.css';

class Videos extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            selectedType: 'series',
            baseUrl: 'https://back.cricwick.net/api/v1/user_api',
            telco: 'telco=mobilink',
            page: 1,
            perPage: 5
        }
    }

    componentDidMount = () => {
        this.loadSeriesRecords();
    }

    loadSeriesRecords = async () => {
        const { hasMore, page, perPage, baseUrl, telco} = this.state;
        //https://back.cricwick.net/api/v1/user_api/get_new_series?page=1&per_page=5&telco=mobilink
        const Url = baseUrl + '/' + 'get_new_series?'
            + 'page=' + page + '&' + 'per_page=' + perPage
            + '&' + telco

        //console.log(Url);
        //console.log(hasMore);

        request
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                    hasMore: (res.length < perPage)
                });
            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }


    handleClick = (selectedType) => {

        if (!selectedType) {
            this.setState({ selectedType: 'live_series' });
        } else {
            this.setState({ selectedType: selectedType });
        }
    }

    renderVideos = (data) => {
        return (
            <div>
                {data.videos.map((video, i) => (
                    <div className="card card-block" key={video.id}>
                        <img
                            className="card-img-top"
                            src={video.video.thumb}
                            alt="noImage"/>

                        <div className="card-body">
                            <span className='video-match-title'>
                                 {video.video.match_obj.title}
                            </span><br />

                            <span className='video-title'>
                                   {video.video.title}
                            </span>
                        </div>

                    </div>
                ))}
            </div>
        )
    }




    render() {

        const { error, isLoaded, seriesData } = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            //------------------------------------------------------------------- 
            return (
                <div>
                    <div className='card card-block mb-1'>
                        <span>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('live_series')} >All Videos</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('upcoming_series')}>Featured</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('recent_series')}>Archives</button>
                        </span>
                    </div>

                    {seriesData.series.map((videos, i) => (
                        <div key={videos.id}>
                            <h6>{videos.title}</h6>
                            <div className='card-columns p-3'>
                                {this.renderVideos(videos)}
                            </div>
                        </div>
                    ))}
                </div>
            );
        }
        //----------------------------------------------------------------------
    }
}

export default Videos;
