import React, { Component }         from 'react';
import LatestNews                   from './latestNews';
import Articles                     from './articles';
import {NavLink, Route,  Switch}    from 'react-router-dom';

class Stories extends Component {

    constructor(props) {
        super(props);

        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            baseUrl: 'https://back.cricwick.net/api',
            page: 1,
            perPage: 5,
        }
    }

      //URL FOR Particular NEWS : https://back.cricwick.net/api/news/2113
      //URL FOR Particular NEWS : https://back.cricwick.net/api/articles/629

    componentDidMount = async ()=> {
        const {match} =  this.props;
        
    }

    componentWillReceiveProps = async (nextProps) =>{
        //  console.log( "Component Will Recevie props - selectedType : "     ,nextProps.location.state.selectedType);
        //  console.log( "Component Will Recevie props - objSelectedStory : " ,nextProps.location.state.objSelectedStory);
        //  console.log( "Component Will Recevie props - showFull   : " ,nextProps.location.state.showFull);

            //   await this.setState({
            //        selectedType     :  nextProps.location.state.selectedType, 
            //        objSelectedStory :  nextProps.location.state.objSelectedStory,
            //        showFull         :  nextProps.location.state.showFull,
            //   });
    }

    loadSeriesRecords = async () => {
        const { page, perPage, baseUrl } = this.state;

        // 1) All Videos :
        //    Base URL =  https://back.cricwick.net/api/news?page=1&page_size=6
        //    Composed =  /news?page=1&page_size=5

        let Url = baseUrl
            + '/'
            + 'news'
            + '?'
            + 'page=' + page
            + '&'
            + 'page_size='
            + perPage

        console.log(Url);

        await request
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState(() => {
                    return {
                        isLoaded: true,
                        isLoading: false,
                        seriesData: res.body,
                        hasMore: (res.body.length < perPage)
                    }
                });
            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    render() {

          const { match } = this.props;

          return (
            <div>
                
                <div className='card card-block pl-2 pt-3 pb-3 mb-1'>
                        {/* <h6 className='menu-buttons-title-heading'>{seriesData.match.series.title}</h6> */}
                         <span className='short-menu-buttons'>
                            <NavLink className='btn btn-default' 
                                     to={`${match.url}/news`}       
                                     activeClassName="short-menu-active" >Latest news     </NavLink>
                            <NavLink className='btn btn-default' 
                                     to={`${match.url}/articles`}   
                                     activeClassName="short-menu-active"  >Articles        </NavLink>
                        </span>
                </div>

                <div>
                      <Switch>
                           <Route exact
                              path={`${match.url}/`}
                              render={(props) => <LatestNews {...props} selectedType="news" />}
                          />
                           
                           <Route exact
                              path={`${match.url}/news`}
                              render={(props) => <LatestNews {...props} selectedType="news" />}
                          />  
                  
                          <Route
                              path={`${match.url}/articles`}
                              render={(props) => <Articles   {...props} selectedType="articles" />}
                          />
                      </Switch>
                  </div> 
            </div>
        );

    }
}

export default Stories;


