import React, { Component } from "react";
import config from "../../config.json";
//import http from '../../services/httpService'
import http from "superagent";
import { library } from '@fortawesome/fontawesome-svg-core';
import { faPlayCircle } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import JWPlayer from 'react-jwplayer';
import ReactPlayer from 'react-player';
import Loading from '../common/loading';

library.add(faPlayCircle);


class FeaturedVideos extends Component {

  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      seriesData: [],
      isLoading: true,
      isLoaded: false,
      hasMore: true,
      error: null,
      viewable_id: this.props.viewable_id,
      videoId: '1',
      video: [],
      tags: [],
      page: 1,
      perPage: 10,
      tagId: 27,
      videoFile:'',
    };

    this.handleChange =  this.handleChange.bind(this);
  }

  //  RECEVING PARAMETERS:-  
  //  viewable_type : 'series',
  //  viewable_id   : series.id,
  //  page          : page,
  //  telco         : telco

  //   let Url = 'https://back.cricwick.net/api/v1/user_api/get_videos_by_series?series_id=197&page=1&tag_id=27&per_page=10&telco=mobilink
  //      tags = https://back.cricwick.net/api/v2/user_api/get_tag_options_by_match?match_id=undefined&telco=mobilink 
  //   Url = Url + `?viewable_type=${series}`
  //             + `&viewable_id=${seriesId}`
  //             + `&page=${page}`
  //             + `&telco=${telco}`

  componentWillUnmount() {
    this._isMounted = false;
    window.removeEventListener('scroll', this.handleScroll);
  }

  componentDidMount = async () => {
    window.addEventListener('scroll', this.handleScroll);
    await this.loadTags();
    await this.loadSeriesRecords();
  };

  componentWillReceiveProps = async (nextProps) => {
    console.log('component Will Recevie Props', nextProps.viewable_id);
    await this.setState({
      viewable_id: nextProps.viewable_id
    });
  }

  renderTagsDropDown = async () => (
    <div className='dropdown'>
      <button id='btn_tags' className='btn btn-primary dropdown-toggle' type='button'>HighLights
         <span className='caret'></span></button>
      <ul className='dropdown'>
        <li><a href='https:/'>Show All    </a></li>
        <li><a href='https:/'>Ball by Ball</a></li>
        <li><a href='https:/'>4s          </a></li>
        <li><a href='https:/'>6s          </a></li>
        <li><a href='https:/'>Wickets     </a></li>
        <li><a href='https:/'>HighLights  </a></li>
      </ul>
    </div>
  );

  loadSeriesRecords = async () => {
    // const { viewable_type, viewable_id, page } = this.props
    const { viewable_id, page, perPage, seriesData, tagId } = this.state

    const Url = config.apiEndPoint
      + '/v1/user_api/get_videos_by_series?'
      + 'series_id=' + viewable_id
      + '&page=' + page
      + '&tag_id=' + tagId
      + '&per_page=' + perPage
      + '&telco=' + config.telco

    console.log(Url);

    await http
      .get(Url)
      .set('Accept', 'application/json')
      .then(res => {
        this.setState({
          isLoading: false,
          isLoaded: true,
          seriesData: [...seriesData, ...res.body.videos],
          hasMore: (res.body.videos.length < perPage)
        });

      }).catch(error => {
        this.setState({
          isLoaded: false,
          error: error
        });

      })

  }


  loadTags = async () => {
    //Required URL :  https://back.cricwick.net/api/v2/user_api/get_tag_options_by_match?match_id=undefined&telco=mobilink
    const Url = config.apiEndPoint
      + '/v2/user_api/get_tag_options_by_match?'
      + 'match_id=' + 'undefined'
      + '&telco=' + config.telco

    console.log(Url);

    await http
      .get(Url)
      .set('Accept', 'application/json')
      .then(res => {
        this.setState({
          isLoading: false,
          isLoaded: true,
          tags: res.body,
        });

      }).catch(error => {
        this.setState({
          isLoaded: false,
          error: error
        });

      })

  }



  handleScroll = async () => {
    const { hasMore, page } = this.state;
    const offset = (document.documentElement.scrollTop + window.innerHeight);
    const height = document.documentElement.offsetHeight;

    if (!hasMore) {
      //console.log("offset : ", Math.ceil(offset));
      // console.log("offset : ", Math.floor(offset));
      // console.log("height : ", height);

      if (Math.floor(offset) === height) {
        console.log("Inside Scroll");
        console.log("HasMore : ", hasMore, " Page:", page);
        await this.setState({ page: this.state.page + 1 });
        await this.loadSeriesRecords();
      }
    } else {
      console.log('End of list....');
    }
  }

  handleClick = async (video) => {
    await this.setState({ videoId: video.id, videoFile:video.video.video_file  ,video: video })
    console.log('Clicker on Video', this.state.videoId);
    console.log('Clicker on Video', this.state.videoFile);
  }

  handleChange =  async(e) =>{
     console.log("Selected", e);
  }

  //  https://back.cricwick.net/api/v2/view_lists/
  //  get_list_items_from_viewable?viewable_type=series&
  //  viewable_id=156&page=1&telco=mobilink

  render() {
    const { seriesData, error, isLoading, tags, videoFile , video } = this.state;
    console.log(video.video);

    if (error) {
      return <div> Error : {error.message} </div>;
    } else if (isLoading) {
      return (<Loading />);
    } else {
      return <div>
        <div className="card p-4" id="JwPlayer">
           <ReactPlayer  url={videoFile}  playing controls='true' width='100%' height='100%' />
           <h6>{video.title}</h6>

          <div className="dropdown" >
            <button id="dropdownMenuButton" className="btn btn-primary dropdown-toggle dropdown-button" type="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
              HighLights
                <span className="caret pl-2" />
            </button>
              <ul className="dropdown-menu dropdown-menu" aria-labelledby="dropdownMenuButton" onClick={e => this.handleChange(e.target.name)}>
                   {tags.tags.map((tag, i)=>(
                     <li key={i} className='dropdown-item' href="https:/" id={tag.id}>{tag.label}</li>
                  ))}
              </ul>
          </div>
        </div>

          {seriesData.map((video, i) => (
            <div key={video.id} className="flex-d">
              <div
                className="card mb-1 p-0"
                onClick={() => this.handleClick(video)}
              >
                <div className="card-body p-3">
                  <div className="container">
                    <div className="row">
                      <div className="col-3 card-col-for-image">
                        <img
                          className="card-image-small"
                          src={video.video.thumb}
                          alt="no Iamge"
                        />

                        <div className="play-button-small">
                          <FontAwesomeIcon
                            color="white"
                            icon={"play-circle"}
                            size="4x"
                          />
                        </div>
                      </div>

                      <div className="col-9 card-col-for-text">
                        <h6 className="cardsmall-col-heading-black">
                          {video.video.title}
                        </h6>
                        <h6 className="cardsmall-col-sub-heading-gray">
                          {video.video.match_desc}
                        </h6>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>;
    }
  }
}

export default FeaturedVideos;
