import React, { Component } from 'react';
import MatchSummary   from './matchSummary';
import MatchBallByBall from './matchBallByBall';
import MatchVideos from './matchVideos';
import MatchSquads from './matchSquads';

class Summary extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            over: null,
            selectedType: 'summary',
            baseUrl: 'https://back.cricwick.net/api',
        };

    }


    handleClick = (selectedType) => {

        if (!selectedType) {
            this.setState({ selectedType: 'summary' });
        } else {
            this.setState({ selectedType: selectedType });
        }

    }

   

    render() {
        const {selectedType} = this.state;

     
            return (
                <div>
                    <div className='card card-block mb-1'>
                        <span>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('summary')} >Summary</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('score-card')}>Scorecard</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('videos')}>Videos</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('ball_by_ball')}>Ball by Ball</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('squads')}>Squads</button>
                        </span>
                    </div>

                    {selectedType === 'summary' &&
                        <div className='card p-3 mb-1'>
                            <MatchSummary />
                        </div>
                    }
                          
                    {selectedType === 'score-card' &&
                        <div className='card p-3 mb-1'>
                             Scord Card
                        </div>
                    }

                    {selectedType === 'videos' &&
                        <div className='card p-3 mb-1'>
                            <MatchVideos />
                        </div>
                    }

                    {selectedType === 'bal-by-ball' &&
                        <div className='card p-3 mb-1'>
                            <MatchBallByBall />
                        </div>
                    }       
                 
                    {selectedType === 'squads' &&
                        <div className='card p-3 mb-1'>
                             <MatchSquads />
                        </div>
                    }       



                  </div>
            );
        }
    }

export default Summary;
