import React, { Component } from 'react';
import request from 'superagent';
import Moment from 'moment';
import DetailedNews from './detailedNews';
import '../../assets/css/videos/videos.css';
import '../../assets/css/common/videoCards.css'


class LatestNews extends Component {

    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            selectedType: 'Latest_News',
            seriesData: [],
            baseUrl: 'https://back.cricwick.net/api',
            page: 1,
            perPage: 5,
            news: [],
            showFull: false,
            hideOther: false

        }
    }

    
    componentDidMount = async () => {
        await this.loadSeriesRecords();
    }

    loadSeriesRecords = async () => {
        const { page, perPage, baseUrl } = this.state;

        // 1) All Videos :
        //    Base URL =  https://back.cricwick.net/api/news?page=1&page_size=6
        //    Composed =  /news?page=1&page_size=5

        let Url = baseUrl
            + '/'
            + 'news'
            + '?'
            + 'page=' + page
            + '&'
            + 'page_size='
            + perPage

        console.log(Url);

        await request
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                    hasMore: (res.body.length < perPage)
                });

            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    handleClick = (news) => {
        this.setState({
            showFull: true, news
        });
    }

    changeProps(){
        this.setState({ showFull: true});
        console.log(this.state.showFull);
    }


    hideDetail = () => {
        this.setState({
            showFull: !this.state.showFull, hideOther: true
        });
    }

    renderDetailedNews =  (news) => {
        const newsDetail =  this.state.seriesData.data.filter(x => x.id === news.id)
        this.setState({news :  newsDetail});
        console.log("Called From Render Detail News Method : ", newsDetail);
        return (
            <div>from render Detailed News</div>
        );
    }

    render() {
        const { isLoaded, error, seriesData, showFull, news } = this.state;
       
        console.log("Status : ", showFull);

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            return (
                <div>
                    
                    <div id='news' hidden={!showFull} style={{ cursor: 'pointer' }}
                        onClick={() => this.hideDetail()}>
                          <DetailedNews news={news}/>
                    </div>

                    {seriesData.data.map((news, i) => (
                        <div className="flex-d" key={news.id} >
                            <div className="card mb-1 p-2" style={{ cursor: 'pointer' }}
                                onClick={() => this.handleClick(news)}>
                                <div className='card-body p-0'>
                                    <div>
                                        <img className='card-videos-small'
                                            src={news.image}
                                            alt="no Iamge" />
                                    </div>

                                    <div>
                                        <div className='cardsmall-col-sub-heading-gray'>
                                            <span> {news.series_name} </span>
                                            <span> {Moment(new Date(news.created_at)).format("DD MMM,YYYY")} </span>
                                        </div>

                                        <div className='cardsmall-col-sub-heading-gray' >{news.title}</div>
                                        
                                        <div className='cardsmall-col-bodytext-black' style={{ width: 100 + "%" }} > 
                                             {/* {news.body.substr(5, 200)}  */}
                                             <span dangerouslySetInnerHTML={{ __html: news.body }}/>
                                        </div>

                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            );
        }
    }
}

export default LatestNews;
