import React, { Component } from 'react';
import '../../assets/css/home/homeCenterPanel.css';
import SeriesHomeCenter from './seriesHomeCenter';
//import axios from 'axios';

class HomeCenterPanel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            homeContents : [],
            tempContents : [],
            series : [],
        };
    }

    componentDidMount() {
         
        // fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
        //Home Series Api  
        fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home&web_user=1&page=1")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        homeContents: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

//    const genericComponent = (() => (
//        <div>
//            this is match component
//        </div> 
//     ));



    render() {
        const { error, isLoaded, homeContents, tempContents  } = this.state;
        
        let tempContents1 =  homeContents.filter(x => x.type !== 'generic-home');
        
        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
          
        return(
          <div>
                {tempContents1.map((item, i) => (
                    <div key={i}>
                        {item.title}
                        <div>
                            {item.data.map((item, i) => (
                              <div key={i}>
                                    {item.type}
                                    {item.data.map((item,i)=>(
                                        <div key={i}>
                                            {item.id}
                                        </div>
                                    ))}   
                              </div>   
                            ))}
                        </div>
                    </div>     
              ))}
          </div> 
        )                 
      }
    }
}

export default HomeCenterPanel;
