import React, { Component } from 'react';
import request from 'superagent';

class LatestNews extends Component {
    state = {
        isLoaded: true,
        isLoading: false,
        hasMore: true,
        error: null,
        seriesData: [],
        baseUrl: 'https://back.cricwick.net/api',
        page: 1,
        perPage: 5,
        selectedType: 'Latest_News',
    }

    componentDidMount() {
        this.loadSeriesRecords();
    }

    loadSeriesRecords = async () => {
        const { page ,perPage, baseUrl } = this.state;
   
           // 1) All Videos :
           //    Base URL =  https://back.cricwick.net/api/news?page=1&page_size=6
           //    Composed =  /news?page=1&page_size=5
        

        let Url =  baseUrl 
                   + '/'
                   + 'news'
                   + '?' 
                   + 'page=' + page
                   + '&'
                   + 'page_size='
                   + perPage     

        await request
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                    hasMore: (res.body.length < perPage),
                });


            }).catch(error => {
                this.setState({
                    error,
                    isLoaded: false
                });

            })
    }

    render() {
        const { isLoaded, error, seriesData } = this.state;
        
        if (error) {
            return (<div> Error : {error.message} </div>)
        } else if (!isLoaded) {
            return (<div> Loading Records </div>)
        } else {
            return (
                <div>
                   {seriesData.data.map((news, i)=>(
                      <div className='card'> 
                         {news.title} <br/>
                         {news.body}  <br/>
                     </div>
 
                   ))}
                </div>
            );
        }
    }
}

export default LatestNews;
