import React, { Component }         from 'react';
import LatestNews                   from './latestNews';
import Articles                     from './articles';
import {NavLink, Route,  Switch}    from 'react-router-dom';

class Stories extends Component {

    constructor(props) {
        super(props);

        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            baseUrl: 'https://back.cricwick.net/api',
            page: 1,
            perPage: 5,
            selectedType:'',
            id:0,
        }
    }

    //URL FOR Particular NEWS : https://back.cricwick.net/api/news/2113
    //URL FOR Particular NEWS : https://back.cricwick.net/api/articles/629

    componentDidMount = async ()=> {
        const {match} =  this.props;
        

        if(match.params.id){
             console.log("selected", match.params.selectedType, match.params.id);
             this.setState(()=>{
                return{
                     selectedType:match.params.selectedType,
                     id  :match.params.id 
                }   
             })
        }    
        else{         
            this.setState(()=>{
                console.log("not selected");
                return{
                     selectedType:'',
                               id: 0 
                }   
             })
        }    

        console.log("Propertie from Stories : ", match.params.id, this.state.id);

    }

    render() {
          const {selectedType, id } = this.state;
          const { match } = this.props;

          return (
            <div>
                
                <div className='card card-block pl-2 pt-3 pb-3 mb-1'>
                        {/* <h6 className='menu-buttons-title-heading'>{seriesData.match.series.title}</h6> */}
                         <span className='short-menu-buttons'>
                            <NavLink className='btn btn-default' 
                                     to={`${match.url}/news`}       
                                     activeClassName="short-menu-active">Latest news     </NavLink>
                            <NavLink className='btn btn-default' 
                                     to={`${match.url}/articles`}   
                                     activeClassName="short-menu-active">Articles        </NavLink>
                        </span>
                </div>

                <div>
                      <Switch>
                          <Route exact
                              path={`${match.url}/`}
                              render={(props) => <LatestNews {...props} selectedType={selectedType} selected_id={id} />}
                          />

                         <Route exact
                              path={`${match.url}/news`}
                              render={(props) => <LatestNews {...props} selectedType={selectedType} selected_id={id} />}
                         />  
                  
                         <Route
                              path={`${match.url}/articles`}
                              render={(props) => <Articles   {...props} selectedType={selectedType} selected_id={id} />}
                         />

                      </Switch>
                  </div> 
            </div>
        );

    }
}

export default Stories;


