import React, { Component } from 'react';
import request              from  'superagent';
import axios                from 'axios';

class Series extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            baseUrl: 'https://back.cricwick.net/api/v2/series',
            page: 1,
            perPage: 10,
        }

    }

   

    componentDidMount() {
        this.loadSeriesRecord();
    }

    loadSeriesRecord = () => {
        const {seriesData, hasMore, page, perPage} =  this.state;
          // fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
          //Home Series Api  
      
          const Url = this.state.baseUrl + '/'
                      + '?page=' + page
                      + '&per_page=' + perPage;

          //console.log(Url);
          console.log(hasMore);
          
          request
              .get(Url)
              .set('Accept','application/json')
              .then(res => {
                  this.setState({
                      isLoaded: true,
                      isLoading: false,
                      seriesData: [...seriesData, ...res.body],
                      hasMore: (res.length < perPage)
                  });
                
                  console.log(this.state.seriesData); 
              })
              .catch(error => {
                  this.setState({
                      isLoaded: false,
                      error
                  });
             });
      }

    render() {
        return (
            <div>
                <h6>test</h6>
                   {console.log(this.state.seriesData)}
            </div>
        );
    }
}

export default Series;
