import React, { Component } from "react";
import '../../assets/css/home/matchHeader.css';

class MatchHeader extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            liveMatches: [],
            selectedClassName : ''
        };
    }

    componentDidMount() {

        fetch("http://back.cricwick.net/api/v2/main/live_matches")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        liveMatches: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

   
    headerTextMatchStatusClass = (status) =>{
        const isClass = status === 'Live' ? 'header-text-match-status-live' : 'header-text-match-status-normal';
        return isClass; 
    }

    render() {

        const { error, isLoaded, liveMatches } = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            return (
              <div className="scrolling-wrapper">  
                    
                    {liveMatches.live_matches.map(match => (
                        <div className="card card-for-scroll" key={match.id} >
                            <div className="card-content p-1">
                                  
                                   {match.title} -  
                                   <span className={this.headerTextMatchStatusClass('Live')}>
                                      {match.match_state} <br/>
                                   </span>
                                                                    
                                   <img className='header-flag-image' 
                                        src={match.teamA.flag_url} alt="no Iamge"/>
                                        <span className = "header-text-teams-shortname">  
                                            {match.teamA.short_name}
                                        </span>  
                                   <br/>
                                   <img className='header-flag-image' 
                                        src={match.teamB.flag_url} alt="no Iamge"/>
                                        <span className = "header-text-teams-shortname">  
                                            {match.teamB.short_name}
                                        </span>        
                            </div>
                        </div>
                    ))}

               </div>
             );
        }
    }
}

export default MatchHeader;
