import React, { Component } from 'react';   
import http from 'superagent';
import '../../assets/css/summary/summary.css';

//https://back.cricwick.net/api/v3/innings/5087/get_previous_overs?over_number=42

class MatchBallByBall  extends Component {

    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            over: null,
            selectedType: 'summary',
            baseUrl: 'https://back.cricwick.net/api',
        };

    }

    componentDidMount() {
       const {match} =  this.props 
        
        this.setState({seriesData : match })
        // this.loadSeriesRecords();
        // this.state.seriesData['live_series'].map((item,i)=>(
        //       console.log(item)
        // ));
        //console.log( "From Component Did Mount : " , this.state.seriesData);
    }

    loadSeriesRecords = async () => {
        // const { baseUrl } = this.state;
        // const { match } = this.props;

        // console.log("Match from Parent Object : " , match);
        // fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
        //Home Series Api  

        // const Url = baseUrl + '/'
        //     + 'v3'
        //     + '/'
        //     + 'innings'
        //     + '/'
        //     +  inning.id
        //     + '/'
        //     + 'get_previous_overs'
        //     + '?'
        //     + 'over_number' + overNumber 

        // console.log(Url);
        // //console.log(hasMore);

        // http
        //     .get(Url)
        //     .set('Accept', 'application/json')
        //     .then(res => {
        //         this.setState({
        //             isLoaded: true,
        //             isLoading: false,
        //             seriesData: res.body,
        //         });
        //     })
        //     .catch(error => {
        //         this.setState({
        //             isLoaded: false,
        //             error
        //         });
        //     });
    }

    handleClickOnOver = (over) => {
        this.setState({ over: over });
    }  


    selectedOverDetail = () => {
        const {over} = this.state;

        return (
            <div className='card card-block p-4 mb-1'>
                {over.balls.map((ball, i) => (
                  <div style={{width : 100 + "%"}}>   
                    <div key={i}>
                        <div style={{float:'left', width:20 + '%' , paddingBottom: 8 + 'px'}}>
                            <span className='badge badge-primary'>
                                {this.renderBallScore(ball)}
                            </span> <br />
                            <span className='commentry-text'>
                                {ball.title}
                            </span> <br />
                        </div>

                        <div style={{float:'right', width:80 + '%', paddingBottom: 8 + 'px'}} >
                            <span className='commentry-text'> {ball.commentary} </span>
                        </div>
                    </div > 
                     
                     <br/> 
                     <br/>
                     <hr/>
                         
                  </div>  
                ))}
            </div>
        )
    }


    renderOversDetail = (overs) => {
        //https://back.cricwick.net/api/v3/innings/5068/get_previous_overs?over_number=1
        
        return (
            overs.map((over, i) => (
                <div key={i} className='card card-for-scroll p-1' onClick={() => this.handleClickOnOver(over)} style={{cursor:'pointer'}} >
                    <div key={i} className='uppercase'> {over.title} </div>
                     {over.balls.map((ball, i) => (
                          <span key={i} className='badge badge-primary'> 
                                {this.renderBallScore(ball)}
                         </span> 
                    ))}
                </div>
            ))
        );   

    }


    render() {
        const { error, isLoaded, seriesData, overs } = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {

            return (
                <div>
                    <div className='scrolling-wrapper mb-1'>
                         {this.renderOversDetail(seriesData.overs)}
                    </div>

                    <div>
                        {overs && this.selectedOverDetail()}
                    </div>
                </div>
            );
        }
    }
}
 
export default MatchBallByBall;
