import React, { Component } from 'react';
import request              from 'superagent';
import '../../assets/css/videos/videos.css';

class Videos extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            allVideos: [],
            featureVideos:[],
            archiveVideos:[],
            selectedType: 'All_Videos',
            baseUrl: 'https://back.cricwick.net/api/v1',
            telco: 'telco=mobilink',
            page: 1,
            perPage: 5
        }
    }

    componentDidMount = async () => {
        this.loadSeriesRecords(this.state.selectedType);
    }

    loadSeriesRecords = async (selectedType) => {
        const { hasMore, page, perPage, baseUrl, telco} = this.state;
        //https://back.cricwick.net/api/v1/user_api/get_new_series?page=1&per_page=5&telco=mobilink
        
        let Url='';
        // 1) All Videos :
        //    Base URL =  https://back.cricwick.net/api/v1
        //    Composed =  /user_api/get_new_series?page=1&per_page=5&telco=mobilink
     
        if (selectedType === "All_Videos") {
            Url = baseUrl + "/user_api"
                + "/"
                + "get_new_series" + "?"
                + "page="
                + page
                + "&"
                + "per_page=" + perPage
                + "&" + telco
        }
     
        // 2) Feature videos:
        //    Base URL =  https://back.cricwick.net/api/v1
        //    Composed =  /video/get_by_video_type/in_house_content/1/5?telco=mobilink
       
        if (selectedType === "Featured") {
            Url = baseUrl + "/video"
                + "/"
                + "get_by_video_type" + "/"
                + "in_house_content"   + "/"
                + page
                + "/"
                + perPage
                + "?" + telco
        }      

        //3) Arcived:
        //   Base URL =  https://back.cricwick.net/api/v1
        //   Composed =  /video/get_by_video_type/archive/1/5?telco=mobilink
        
        if (selectedType === "Archives") {
            Url = baseUrl + "/video"
            + "/"
            + "get_by_video_type" + "/"
            + "archive"            + "/"
            + page
            + "/"
            + perPage
            + "?" + telco
        }      

       console.log(selectedType ,Url);
        //console.log(hasMore);
       await request
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    allVideos: res.body,
                    hasMore: (res.length < perPage)
                });
               
            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    handleClick = (videosType) => {
        if (!videosType) {
            this.setState({ selectedType: 'All_Videos' });
            this.loadSeriesRecords();
        } else {
            this.setState({ selectedType :  videosType });
            console.log(this.state.selectedType);
            this.loadSeriesRecords();
        }
    }

    renderVideos = (data) => {
     
        const {selectedType} =  this.state;

        if (selectedType === "All_Videos") {
            return (
                <div>
                    {data.videos.map((video, i) => (
                        <div className="card card-block videoCard" key={video.id}>
                            <img
                                className="card-img-top"
                                src={video.video.thumb}
                                alt="noImage" />

                            <div className="card-body p-2">
                                <span className='video-match-title'>
                                    {video.video.match_obj.title}
                                </span><br />

                                <span className='video-title'>
                                    {video.video.title}
                                </span>
                            </div>
                        </div>
                    ))}
                </div>
            )
        }

        if (selectedType === "Featured") {
            return (
                <div>
                    {data.map((video, i) => (
                        <div className="card card-block videoCard" key={video.id}>
                            <img
                                className="card-img-top"
                                src={video.video.thumb}
                                alt="noImage" />

                            <div className="card-body p-2">
                                <span className='video-match-title'>
                                    {video.video.match_obj.title}
                                </span><br />

                                <span className='video-title'>
                                    {video.video.title}
                                </span>
                            </div>
                        </div>
                    ))}
                </div>
            )
        }

        if (selectedType === "Archives") {
             console.log(this.state.allVideos);               
        } 
    }

    render() {

        const { error, isLoaded, allVideos, selectedType} = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            //------------------------------------------------------------------- 
            return (
                <div>
                    <div className='card card-block mb-1'>
                        <span>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => {
                                 this.setState({selectedType:'All_Videos'});
                                 this.loadSeriesRecords('All_Videos'); 
                            }}>All Videos</button>
                            
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('Featured')}>Featured</button>
                            
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => {
                                                                                                  this.setState({selectedType:'Archives'});
                                                                                                  this.loadSeriesRecords('Archives'); 
                                                                                                 }}>Archives</button>
                        </span>
                    </div>

                    {selectedType === 'All_Videos' &&
                            allVideos.series.map((videos, i) => (
                                <div key={videos.id}>
                                    <h6>{videos.title}</h6>
                                    <div className='card-columns m-1 p-0'>
                                        {this.renderVideos(videos)}
                                    </div>
                                </div>
                            ))
                    }
                  
                    {selectedType === 'Featured' &&
                                <div className='card-columns m-1 p-0'>
                                   {this.renderVideos(allVideos)}
                                </div>
                    }

                    {selectedType === 'Archives' &&
                                <div className='card-columns m-1 p-0'>
                                     {this.renderVideos(allVideos)}
                                </div>
                    }  




                </div>
            );
        }
        //----------------------------------------------------------------------
    }
}

export default Videos;
