import React, { Component } from 'react';
import '../../assets/css/home/homeCenterPanel.css';
import RenderMatch from './renderMatch';

class Match extends Component {
    constructor(props) {
        super(props)
        this.state = {
            match: '',
            matchType: '',
        }
    }

    printCardHeader(printSeriesTitle, seriesTitle) {
        if (printSeriesTitle === 0) {
            return <h6 className="card-title">{seriesTitle}</h6>
        }
        return;
    }

    render() {
        const { match, matchType, printSeriesTitle , seriesTitle} = this.props;
        if (matchType === 'matches') {
            return ( 
                <div className="card card-block mb-1 p-4 " style={{ minHeight: 75 + "px" }}>
                    <div className="card-body p-0 ">
                        {this.printCardHeader(printSeriesTitle, seriesTitle)}
                        <RenderMatch
                            match={match}
                            matchType={matchType}
                        />
                    </div>
                </div>
            );      
        }

