import React, { Component } from 'react';
import '../../assets/css/home/homeRankingICC.css';

class RankingContentsIdentifire extends Component {
  constructor(props) {
    super(props);
    this.state = {
        matchType : 'odi',
    };
  }

  handleClick=(rankingType)=>{
      console.log(rankingType);
     
      if (rankingType === 'odi') {
          console.log('ODI');
      }

      if (rankingType === 't20') {
          console.log('T20');
      }

      if (rankingType === 'test_') {
        console.log('test_');
      }
      
  }

  renderRanking = (data) => {
    return (
      <div>
        {data.map((player, i) => (
          <div key={i}>
            <div key={i} className="card mycard p-2 text-center">
              <div className="card-title">
                <img
                  className="img"
                  src={player.player_display_picture}
                  alt="noImage"
                />
              </div>

              <div className="card-title">
                <span className="player-rating-number">{player.rating}</span>{" "}
                <br/>
                <span>Rating</span>
                
                <br/> <br/>
                <span className='home-ranking-player-name'> {i + 1} {" - "} {player.player_name} </span> 
                <br />
                {player.team_name}
             
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

    render() {

        const { contents } = this.props;

        if (!contents.data) return "";

        return (
            <div className="card p-3 mb-2">
                <h6 className="card-title"> ICC RANKING</h6>

                   <span> 
                       <button className='btn btn-default' onClick={()=>this.handleClick('odi')} >ODI</button>
                       <button className='btn btn-default ml-2' onClick={()=>this.handleClick('t20')}>T20</button>
                       <button className='btn btn-default ml-2' onClick={()=>this.handleClick('test_')}>TEST</button>
                   </span>

                <h6 className="card-title">ALL ROUNDERS</h6>
                <div className="card-columns">
                    {this.renderRanking(contents.data.odi.all_rounder)}
                </div>

                <h6 className="card-title">BOWLERS</h6>
                <div className="card-columns">
                    {this.renderRanking(contents.data.odi.bowler)}

                </div>

                <h6 className="card-title">BATSMEN</h6>

                <div className="card-columns">
                    {this.renderRanking(contents.data.odi.batsmen)}

                </div>
            </div>
        );
    }
}

export default RankingContentsIdentifire;
