import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { library } from '@fortawesome/fontawesome-svg-core';
import { faSpinner, faSpin } from "@fortawesome/free-solid-svg-icons";
library.add(faSpinner, faSpin);

const Loading = () => (
    <div style={{textAlign:'center'}} > 


         {/* <img style={{height:60+'px', width:60+'px'}}  
               src={require('../../assets/css/common/loading.gif')} alt=''/> */}
                <FontAwesomeIcon color="gray"
                  icon={[faSpinner, faSpin]}
                  transform={{ rotate: 42}}
                  size="3x"
               /> 
    </div>



);
  
export default Loading;
