import React , {Component} from 'react';
import '../../assets/css/home/homeCenterPanel.css';
class HomeCenterPanel extends Component{
    
    constructor(){
         this.stete={
            hasError : null,
            isLoaded : false,  
            liveMatches:[]
         }   
    }

    componentDidMount() {

        fetch("http://54.245.34.7:8080/api/v2/main/live_matches")
            .then(res => res.json())
            .then((result) => {
                this.setState({ liveMatches: result });
            }, (error) => {
                this.setState({ isLoaded: true, error });
            });
    }



    render(){
         return(
             <div>
                 <div className="card flex-row flex-wrap">
                      
                    <div className="card-header p-0"> 
                        <img className="card-img-top" src={require("../../assets/images/img2.jfif")} alt="no Image" />
                    </div>
                    
                     <div className="card-block px-1">
                           <h6 className="card-title p-2">Pakistan vs Newzeland</h6>
                            <p className='card-text p-1'>
                               match details, match details,
                               match details, match details,<br/>
                               match details, match details, 
                               match details, match details,
                               match details, match details, <br/>
                               match details, match details,
                            </p>
                     </div>
                 </div>
                 
             </div>
         );
    }
}


export default HomeCenterPanel;
