import React, { Component } from 'react';
import LatestNews from './latestNews';
import Articles from './articles';

class Stories extends Component {

    constructor(props) {
        super(props);

        this.state = {
            selectedType     : this.props.location.state.selectedType,
            objSelectedStory : this.props.location.state.objSelectedStory,
            showFull         : this.props.location.state.showFull,
        }
    }

    componentDidMount = async ()=> {

        //Getting Props form Parent Component
        // const {obj_match , type, showFull} = this.props.location.state;
      
        // this.setState((state) =>({
        //           objMatch : obj_match,
        //           type : type,
        //           showFull:showFull,   
        // }));

        console.log(this.state.selectedType);
        // this.setState({
        //     selectedType : selectedType,
        //        objMatch  : obj_match,
        //            type  : type,
        //         showFull : showFull}, 
        // ()=>{
        //        console.log("State will update in this function...", this.state)    
            
        //  }); 


    }

    componentWillReceiveProps = async (nextProps) =>{
         console.log( "Component Will Recevie props - selectedType : "     ,nextProps.location.state.selectedType);
         console.log( "Component Will Recevie props - objSelectedStory : " ,nextProps.location.state.objSelectedStory);
         console.log( "Component Will Recevie props - showFull   : " ,nextProps.location.state.showFull);

              await this.setState({
                   selectedType     :  nextProps.location.state.selectedType, 
                   objSelectedStory :  nextProps.location.state.objSelectedStory,
                   showFull         :  nextProps.location.state.showFull,
              });
    }

    handleClick = async (selectedType) => {
         await this.setState({ selectedType: selectedType, showFull:false});
    }

    render() {

        const { selectedType, objSelectedStory, showFull } = this.state;

          console.log(objSelectedStory);
        
          return (
            <div>
                <div className='card'>
                    <span>
                        <button className='btn btn-link' onClick={() => this.handleClick('Latest_News')}>Latest News</button>
                        <button className='btn btn-link' onClick={() => this.handleClick('Articles')}>Articles</button>
                    </span>
                </div>

                {selectedType === 'Latest_News' &&
                    <div>
                        <LatestNews selectedType = {selectedType} objSelectedStory={objSelectedStory}  showFull={showFull}  />   
                        
                    </div>
                }

                {selectedType === 'Articles' &&
                    <div>
                        <Articles selectedType = {selectedType} objSelectedStory={objSelectedStory}  showFull={showFull}/>
                    </div>
                }

            </div>
        );

    }
}

export default Stories;
