import React, { Component } from 'react';
import Moment from 'moment';

import '../../assets/css/home/homeCenterPanel.css';

class Match extends Component {
    constructor(props) {
        super(props)
        this.state = {
            matches: '',
            matchType: '',
            match:'',
        }
    }

    renderMatchTeamATag(match){
        let tagText ='';
        
        // if(match.innings.length > 0) {
        //   tagText = 
        //   <span> 
        //       {match.innings[0].runs}{ " "}/{ " "}
        //       {match.innings[0].wickets} {" "} 
        //       ({match.innings[0].overs}) 
        //   </span>
        // }

        return tagText;
    }

    renderMatchTeamBTag(match){
        let tagText = '';

        // if(!match.innings){
        //    console.log(match.innings)
        //    tagText =  <span></span>
        //    return 
        // }   

        // if (match.innings.length >= 1) {
        //     tagText =
        //         <span>
        //             {match.innings[1].runs}{" "}/{" "}
        //             {match.innings[1].wickets} {" "}
        //             ({match.innings[1].overs})
        //         </span>
          
        // }
        return tagText;
   }



    render() {
        const { matches, match, matchType } = this.props;

       matches.map((match,i) =>{
                  console.log(match);
           return match;                                
       }); 

        if (matchType === 'matches') {
            return (
                <div className="card card-block mb-1">

                    {/* Following Code Render Flags or team A */}
                    <img className='header-flag-image' style={{width:25+"px", height:25+"px"}}
                        src={match.teamA.flag_url} alt="no Iamge" />

                    <span className="header-text-teams-shortname">
                        {match.teamA.short_name}
                    </span>

                    <span className="header-text-teama-score">
                        {this.renderMatchTeamBTag(match)}
                    </span>
                  

                    {/* Following Code Render Flags or team B */}
                    <img className='header-flag-image' style={{width:25+"px", height:25+"px"}}
                        src={match.teamB.flag_url} alt="no Iamge" />
                    <span className="header-text-teams-shortname">
                        {match.teamB.short_name}
                    </span>

                    {/* Following Code Render Score team B */}
                    <span className="header-text-teama-score">
                        {this.renderMatchTeamATag(match)}
                    </span>
                </div>
            );
        }

        if (matchType === 'news') {
            return (
                    <ul>
                        <li style={{marginLeft:0, padding:0}}>{match.title}</li>
                    </ul>
            );
        }

        if (matchType === 'videos') {
            return (
                <div className="media">
                    <div className='image'>
                        <img className='card-img-bottom'
                            src={match.med_image}
                            style={{ height: 100 + "px", width: 150 + "px", align: 'right' }}
                            alt="no Iamge" />
                    </div>

                    <div className="content">
                        {match.title}
                    </div>
                </div>

            );
        }

        if (matchType === 'article') {
            return <React.Fragment>
                <div className="media">
                  <div className="image">
                    <img className="card-img-bottom" src={match.image} style={{ height: 100 + "px", width: 150 + "px", align: "right" }} alt="no Iamge" />
                  </div>

                  <div className="content">
                    {match.title}
                    <div className="small-text ">
                      {Moment(new Date(match.created_at)).format("DD MMM, YYYY")}
                    </div>
                  </div>
                </div>
              </React.Fragment>;
        }

        if (matchType === 'tweets') {
            
            return (
                <div className='card tweets mt-2 mb-2' >
                    <span dangerouslySetInnerHTML={{__html : match.body}}></span> 
                </div>
            )
        }

        return (
            <div className="tweets">
                       Other than Match
                        News
            </div>
        );
    }
}
