    //          match.data.map((item, i) => {
    //          console.log("Matches : ",item.id);
    //          tagText = <div> {item.title} </div>;
    //          return tagText; 
    //          });
    //     }

    //     if (match.type === 'videos') {
    //         match.data.map((item, i) => {
    //             console.log("Videos : ",item.title);
    //             tagText = item.title;
    //         });
    //     }

    //     if (match.type === 'news') {
    //         match.data.map((item, i) => {
    //             console.log("News : ",item.title);
    //             tagText = item.title;
    //         });
    //     }

    //     if (match.type === 'articles') {
    //         match.data.map((item, i) => {
    //             console.log("Articles : ",item.title);
    //             tagText = item.title;
    //         });
    //     }






export default Match;
