import React, { Component } from 'react';
import request              from  'superagent';
//import axios                from 'axios';

class Series extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            selectedType:'live_series', 
            baseUrl: 'https://back.cricwick.net/api/v2/series',
            page: 1,
            perPage: 10,
        };

    }

    componentDidMount() {
        this.loadSeriesRecord();
        // this.state.seriesData['live_series'].map((item,i)=>(
        //       console.log(item)
        // ));
        //console.log( "From Component Did Mount : " , this.state.seriesData);
    }

    loadSeriesRecord = () => {
        const {hasMore, page, perPage} =  this.state;
          // fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
          //Home Series Api  
      
          const Url = this.state.baseUrl + '/'
                      + '?page=' + page
                      + '&per_page=' + perPage;

          //console.log(Url);
          console.log(hasMore);
          
          request
              .get(Url)
              .set('Accept','application/json')
              .then(res => {
                  this.setState({
                      isLoaded: true,
                      isLoading: false,
                      seriesData: res.body,
                      hasMore: (res.length < perPage)
                  });

                // this.state.seriesData['live_series'].map((item, i)=>(
                //     console.log(item.title)
                // )) 


              })
              .catch(error => {
                  this.setState({
                      isLoaded: false,
                      error
                  });
             });
      }

    render() {
        const { error, isLoaded, seriesData} = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {

        return (
            <div>
                <div>
                    <span>
                        <button className='btn btn-default mr-2 btn-sm' onClick={() => this.handleClick('live_series')} >Live</button>
                        <button className='btn btn-primary mr-2 btn-sm' onClick={() => this.handleClick('upcoming_series')}>T20</button>
                        <button className='btn btn-primary mr-2 btn-sm' onClick={() => this.handleClick('recent_series')}>TEST</button>
                    </span>
                </div>

                <h6>test</h6>
                   {seriesData['live_series'].map((item,i)=>(
                       <div key={item.id}>{i}</div>
                   ))}
            </div>
        );
    }
  } 
}

export default Series;
