import React, { Component } from 'react';
import request              from 'superagent';
import Moment               from 'moment';
import '../../assets/css/series/series.css';
//import axios                from 'axios';

class Series extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            selectedType:'live_series', 
            baseUrl: 'https://back.cricwick.net/api/v2/series',
            page: 1,
            perPage: 10,
        };

    }

    componentDidMount() {
        this.loadSeriesRecord();
        // this.state.seriesData['live_series'].map((item,i)=>(
        //       console.log(item)
        // ));
        //console.log( "From Component Did Mount : " , this.state.seriesData);
    }

    loadSeriesRecords() {
        const {hasMore, page, perPage, baseUrl} =  this.state;
          // fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
          //Home Series Api  
      
          const Url = baseUrl + '/'
                      + '?page=' + page
                      + '&per_page=' + perPage;

          //console.log(Url);
          //console.log(hasMore);
          
          request
              .get(Url)
              .set('Accept','application/json')
              .then(res => {
                  this.setState({
                      isLoaded: true,
                      isLoading: false,
                      seriesData: res.body,
                      hasMore: (res.length < perPage)
                  });
              })
              .catch(error => {
                  this.setState({
                      isLoaded: false,
                      error
                  });
             });
      }

    handleClick = (selectedType) => {
 
        if (!selectedType) {
            this.setState({ selectedType: 'live_series' });
        } else {
            this.setState({ selectedType: selectedType });
        }
    }

    renderSeries = (data) => {
        return (
            <div>
                {data.map((series, i) => (
                    <div key={series.id}>
                        <span className='video-title'> {series.title} </span>
                        <br/>
                        <span className='video-series-start-end'>
                            {Moment(new Date(series.start_date)).format("DD MMM")} {" - "}
                            {Moment(new Date(series.start_date)).format("DD MMM")}
                        </span>
                        {data.length !== (i + 1) ? <hr /> : ''}
                    </div>
                ))}
            </div>
        )
    }



    render() {
        const { error, isLoaded, seriesData, selectedType} = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {

        return (
            <div>
                <div className='card card-block mb-1'>
                    <span>
                        <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('live_series')} >Live</button>
                        <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('upcoming_series')}>Upcomming</button>
                        <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('recent_series')}>Result</button>
                    </span>
                </div>

                  
                <div className="card card-block p-3 mb-1">
                    {this.renderSeries(seriesData[selectedType])}        
                </div>  
            </div>
        );
    }
  } 
}

export default Series;
