import React, { Component } from "react";
import Moment from 'moment';
//import HeaderLiveMatch from  './headerLiveMatch';
import '../../assets/css/home/matchHeader.css';


class MatchHeader extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            liveMatches: [],
            selectedClassName : ''
        };
    }

    componentDidMount() {

        fetch("http://back.cricwick.net/api/v2/main/live_matches")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        liveMatches: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

   
    headerTextMatchStatusClass = (status) =>{
        const isClass = status === 'Live' ? 'header-text-match-status-live' : 'header-text-match-status-normal';
        return isClass; 
    }

    renderMatchTeamATag(match){
        let tagText ='';
        
        if(match.innings.length > 0) {
          tagText = 
          <span> 
              {match.innings[0].runs}{ " "}/{ " "}
              {match.innings[0].wickets} {" "} 
              ({match.innings[0].overs}) 
          </span>
        }

        return tagText;
    }

    renderMatchTeamBTag(match){
        let tagText = '';

        if(!match.innings[1]){
           console.log(match.innings[1])
           tagText =  <span></span>
           return 
        }   

        if (match.innings.length >= 1) {
            tagText =
                <span>
                    {match.innings[1].runs}{" "}/{" "}
                    {match.innings[1].wickets} {" "}
                    ({match.innings[1].overs})
                </span>
          
        }
        return tagText;
   }

   //This method return the MATCH_STATUS text if the break type is Empty Otherwize return BREAK_TYPE. 
   renderMatchStatus(match) {
        let tagText = '';
        if (match.break_type === "") {
            tagText = <span>
                        {match.match_state}
                      </span>
        } else {
            tagText = <span>
                        {match.break_type}
                      </span>
        }
        return tagText;
   }
   //-------------------------------------------------------------------------------------------------

    render() {
        const { error, isLoaded, liveMatches } = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            return (
              <div className="scrolling-wrapper">  
                    
                    {liveMatches.live_matches.map(match => (
                        <div className="card card-for-scroll" key={match.id} >
                            <div className="card-content p-1">
                                  
                                   {/* Following Code Render Live and Scheduled Matched */}
                                   {/* If Break Type is Empty Then It Will Show  Break Type Otherr Wise MATCH_STATUS */}

                                   <span className={this.headerTextMatchStatusClass(match.match_state)}>
                                       {this.renderMatchStatus(match)}
                                   </span>

                                   {/* Following Code Render Header text for Live-Matches */}
                                   {/* eg:  Title, Venue, Match Start Date */}
                                   {match.title}, {' at '}  <span style={{color:'Gray'}}>{match.venue.title}</span>  
                                   <span>
                                       {" "} {Moment(new Date(match.match_start)).format("DD MMM,")} <br/>
                                   </span>
                                   
                                                                                                      
                                   {/* Following Code Render Flags or team A */}                                 
                                   <img className='header-flag-image' 
                                        src={match.teamA.flag_url} alt="no Iamge"/>
                                        <span className = "header-text-teams-shortname">  
                                            {match.teamA.short_name}
                                        </span>  

                                        <span className = "header-text-teama-score">
                                             {this.renderMatchTeamBTag(match)} 
                                        </span>
                                   <br/>
                                   
                                   {/* Following Code Render Flags or team B */}  
                                   <img className='header-flag-image' 
                                        src={match.teamB.flag_url} alt="no Iamge"/>
                                        <span className = "header-text-teams-shortname">  
                                            {match.teamB.short_name}
                                        </span> 

                                        {/* Following Code Render Score team B */} 
                                        <span className = "header-text-teama-score">
                                             {this.renderMatchTeamATag(match)} 
                                        </span>
                                     
                            </div>
                        </div>
                    ))}

               </div>
             );
        }
    }
}

export default MatchHeader;
