import React, { Component } from 'react';
import request from 'superagent';
import '../../assets/css/home/homeRankingICC.css';

class Ranking extends Component {
    state = {
        error: null,
        isLoaded: false,
        isLoading: false,
        hasMore: true,
        selectedType: 'Latest_News',
        seriesData: [],
        baseUrl: 'https://back.cricwick.net/api',
        page: 1,
        perPage: 5,
        article: [],
        showFull: false,

    }

    componentDidMount = async () => {
        await this.loadSeriesRecords();
    }

    loadSeriesRecords = async () => {
        const { page, perPage, baseUrl } = this.state;

        // 1) All Videos :
        //    Base URL =   https://back.cricwick.net/api 
        //    Composed =  /articles?page=1&page_size=6


        let Url = baseUrl
            + '/'
            + 'articles'
            + '?'
            + 'page=' + page
            + '&'
            + 'page_size='
            + perPage

        console.log(Url);

        await request
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                    hasMore: (res.body.length < perPage)
                });

            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    handleClikc = (article) => {
        this.setState({
            showFull: true, article 
        });
    }

    hideDetail = () => {
        this.setState({
            showFull: !this.state.showFull
        });
    }

    handleClick = (rankingType) => {

        if (rankingType === 'odi') {
            this.setState({ matchType: 'odi', matchTypeToDisplay: 'ODI' });
        }

        if (rankingType === 't20') {
            this.setState({ matchType: 't20', matchTypeToDisplay: 'T20' });
        }

        if (rankingType === 'test_') {
            this.setState({ matchType: 'test_', matchTypeToDisplay: 'TEST' });
        }

    }

    renderRanking = (data) => {
        
        return (
            <div>
                {data.map((player, i) => (
                    <div key={i}>
                        <div key={i} className="card mycard p-2 text-center">
                                <img
                                    className="playerImage"
                                    src={player.player_display_picture}
                                    alt="noImage"
                                />

                            <div className="card-body">
                                <span>Rating</span><br/>
                                <span className="player-rating-number">{player.rating}</span>{" "}

                                <br />
                                <span className='home-ranking-player-name'> {i + 1} {" - "} {player.player_name} </span>
                                <br />
                                {player.team_name}

                            </div>
                        </div>
                    </div>
                ))}
            </div>
        );
    };

    render() {

        const { contents } = this.props;
        const { matchType, matchTypeToDisplay } = this.state;

        //console.log(contents);

        if (!contents.data) return "";

        return (
            <div>

                <div>
                    <span>
                        <button className='btn mybutton btn-default mr-2 btn-sm' onClick={() => this.handleClick('test_')}>TEST</button>
                        <button className='btn mybutton btn-default mr-2 btn-sm' onClick={() => this.handleClick('odi')} >ODI</button>
                        <button className='btn mybutton btn-default mr-2 btn-sm' onClick={() => this.handleClick('t20')}>T20</button>
                    </span>
                </div>

                {/* <!-- This  following Code is for  ODI/T20/TEST Ranking  --> */}
              
                    <div className="card p-3 mb-1">
                        <h6 className="home-ranking-main-title">
                            ICC RANKING -
                                   <span className='home-ranking-main-title-dynamic'>
                                {' '} {matchTypeToDisplay}
                            </span>
                        </h6>

                    
                    <span className="home-ranking-sub-title">
                    ALL ROUNDERS</span>
                    
                        <div className="card-columns">
                            {this.renderRanking(contents.data[matchType].all_rounder)}
                        </div>

                        <h6 className="home-ranking-sub-title">BOWLERS</h6>
                        <div className="card-columns">
                            {this.renderRanking(contents.data[matchType].bowler)}
                        </div>

                        <h6 className="home-ranking-sub-title">BATSMEN</h6>
                        <div className="card-columns">
                            {this.renderRanking(contents.data[matchType].batsmen)}
                        </div>
                    </div>
            </div>
        );
    }
}

export default Ranking;
