import React, { Component } from 'react';
import '../../assets/css/home/homeCenterPanel.css';
import RenderMatch from './renderMatch';

class Match extends Component {
    constructor(props) {
        super(props)
        this.state = {
            match: '',
            matchType: '',
        }
    }
    
    //----------------------------------------------------------- 
    // This Method Print Series Title on the first card as header. 
    printMatchCardHeader(printSeriesTitle, seriesTitle) {
        if (printSeriesTitle === 0) {
            return <h6 className="card-title">
                       {seriesTitle}
                       <hr/> 
                   </h6>
        }
        return;
    }
    
    //-------------------------------------------------------------------------------
    // 1) This Method Print Particular Series video's Footer the first card as header.
    // 2) Further this function will also print the first big and remaining card 
    //    in text and image side by side format.     
    printVideosCardHeader(printSeriesTitle, match) {
        console.log("Video Title Print : ", printSeriesTitle);

        if (printSeriesTitle === 0) {

            return (
                <div className='card mb-1'>
                    <div className='card-body p-3'>
                        <img className='home-center-series-videos-large'
                            src={match.med_image} alt="no Iamge" />
                           <div className="home-center-videos-title">
                               {match.title} 
                           </div>
                    </div>
                </div>
            )

        }
        else {

            return (
                <div className="flex-d">
                    <div className="card block-card">
                        <div style={{ width: 40 + "%", float: 'left' }} >
                             <img className='home-center-series-videos-small'
                                  src={match.med_image} 
                                  alt="no Iamge" />  
                        </div>
{/* 
                        <div style={{ width: 60 + "%", float: 'right' }} >
                            <h6>{match.title} </h6>
                        </div> */}

                    </div>
                </div>
            )

        }
    }

    
    render() {
        const { match, matchType, printSeriesTitle , seriesTitle} = this.props;
        if (matchType === 'matches') {
            return ( 
                <div className="card card-block mb-1" style={{ minHeight: 75 + "px" }}>
                    <div className="card-body p-3">
                        {this.printMatchCardHeader(printSeriesTitle, seriesTitle)}
                        <RenderMatch
                            match={match}
                            matchType={matchType}
                        />
                    </div>
                </div>
            );      
        }

        if (matchType === 'news') {
            return (
                <div>  
                     <ul>
                            <li>{match.title}</li>
                     </ul>
                </div>
            )
        }

        if (matchType === 'videos') {
            return (
                <div>
                    {/* Following function will render videos */}
                    {this.printVideosCardHeader(printSeriesTitle, match)}
                </div>
           )
        }

        // if (matchType === 'article') {
        //     return (
        //         <div className="flex-d">
        //             <div className="card block-card card-home-center-articles">

        //                 <div style={{width:75 +"%", float:'left' }} >
        //                     <h6>{match.title} </h6>
        //                 </div>

        //                 <div  style={{width:25 +"%", float:'left'}} >
        //                     <img style={{ height: 100 + "px", width: 150 + "px" }}
        //                         src={match.image}
        //                         alt="no Iamge" />
        //                 </div>

        //             </div>       
        //         </div>
        //     )
        // }

        // if (matchType === 'tweets') {
            
        //     return (
        //         <div>
        //             <span dangerouslySetInnerHTML={{__html : match.body}}></span> 
        //         </div>
        //     )
        // }

        return (
            <div>
                    videos/Articles/Tweets
            </div>
        )
    }
}
   
export default Match;
