import React, { Component } from "react";
import '../../assets/css/home/matchHeader.css';

class MatchHeader extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            liveMatches: []
        };
    }

    componentDidMount() {

        fetch("http://54.245.34.7:8080/api/v2/main/live_matches")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        liveMatches: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    render() {

        const { error, isLoaded, liveMatches } = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            return (

                <div class="container testimonial-group">
                    <div class="row text-center">
                        <div class="col-xs-4">1</div>
                        <div class="col-xs-4">2</div>
                        <div class="col-xs-4">3</div>
                        <div class="col-xs-4">4</div>
                        <div class="col-xs-4">5</div>
                        <div class="col-xs-4">6</div>
                        <div class="col-xs-4">7</div>
                        <div class="col-xs-4">8</div>
                        <div class="col-xs-4">9</div>
                    </div>
                </div>


            //   <div id="header-container" className="container">  
            //     <div className="cardss v-transition">
            //         {liveMatches.live_matches.map(match => (
            //             <div className="card" key={match.id} >
            //                 <div className="card-content">
            //                     <p>{match.title}</p>
            //                     <p> {match.match_state}</p>
            //                     <p> {match.teamA.short_name} </p>
            //                     <p> {match.teamB.short_name} </p>
            //                 </div>
            //             </div>
            //         ))}  
            //      </div>
            //    </div>
             );
        }
    }
}

export default MatchHeader;
